import React, { useState, useEffect, useRef } from 'react';
import { 
  LinkIcon, 
  MagnifyingGlassIcon, 
  CheckCircleIcon,
  ArrowDownTrayIcon,
  ArrowRightOnRectangleIcon,
  UserCircleIcon,
  SparklesIcon,
  FireIcon,
  XMarkIcon,
  WrenchScrewdriverIcon,
  BookOpenIcon, 
  BookmarkIcon, 
  PlusCircleIcon 
} from '@heroicons/react/24/outline';

const API_BASE = 'http://localhost:8000';

const Spinner = ({ size = 'h-5 w-5', color = 'text-white' }) => (
  <svg className={`animate-spin ${size} ${color}`} xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
  </svg>
);

const RecipeStreamViewer = ({ status, error, data, onReset, currentUser }) => {
  const [showRecipe, setShowRecipe] = useState(false);

  if (error) {
    return (
      <div className="text-center p-6 bg-red-50 border border-red-200 rounded-lg">
        <h3 className="text-xl font-semibold text-red-600">Generation Failed</h3>
        <p className="text-gray-700 my-4 whitespace-pre-wrap">{error}</p>
        <button onClick={onReset} className="bg-gray-700 text-white font-bold py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors">Start Over</button>
      </div>
    );
  }

  const renderRecipe = (recipe) => (
    <div className="text-left font-sans">
      <h2 className="text-3xl font-bold text-gray-800 mb-4">{recipe.title || 'Your Recipe'}</h2>
      <p className="text-gray-600 mb-6">{recipe.description || ''}</p>

      <div className="grid grid-cols-3 gap-4 text-center mb-6">
        {recipe.prep_time && <div className="bg-gray-100 p-3 rounded-lg"><dt className="text-sm font-medium text-gray-500">Prep Time</dt><dd className="text-lg font-semibold text-gray-800">{recipe.prep_time}</dd></div>}
        {recipe.cook_time && <div className="bg-gray-100 p-3 rounded-lg"><dt className="text-sm font-medium text-gray-500">Cook Time</dt><dd className="text-lg font-semibold text-gray-800">{recipe.cook_time}</dd></div>}
        {recipe.servings && <div className="bg-gray-100 p-3 rounded-lg"><dt className="text-sm font-medium text-gray-500">Servings</dt><dd className="text-lg font-semibold text-gray-800">{recipe.servings}</dd></div>}
      </div>

      {recipe.ingredients && (
        <>
          <h3 className="text-xl font-bold text-gray-800 mt-8 mb-4">Ingredients</h3>
          <ul className="list-disc list-inside space-y-2">
            {recipe.ingredients.map((ing, i) => (
              <li key={i} className="text-gray-700">{`${ing.quantity} ${ing.name}`} {ing.notes && <span className="text-gray-500 text-sm">{ing.notes}</span>}</li>
            ))}
          </ul>
        </>
      )}

      {recipe.instructions && (
        <>
          <h3 className="text-xl font-bold text-gray-800 mt-8 mb-4">Instructions</h3>
          <ol className="list-decimal list-inside space-y-4">
            {recipe.instructions.map((inst, i) => (
              <li key={i} className="text-gray-700"><strong className="font-semibold">Step {inst.step}:</strong> {inst.description}</li>
            ))}
          </ol>
        </>
      )}

      {recipe.chef_tips && recipe.chef_tips.length > 0 && (
        <>
          <h3 className="text-xl font-bold text-gray-800 mt-8 mb-4">Chef's Tips</h3>
          <ul className="list-disc list-inside space-y-2">
            {recipe.chef_tips.map((tip, i) => <li key={i} className="text-gray-700">{tip}</li>)}
          </ul>
        </>
      )}
    </div>
  );

  const handleSaveRecipe = async () => {
    if (!currentUser || !data) return;

    try {
      const response = await fetch(`${API_BASE}/api/v1/recipes/save`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({
          source_url: " streamed-recipe", // Placeholder, this should be the video URL
          recipe_content: data
        })
      });

      if (response.ok) {
        alert("Recipe saved successfully!");
      } else {
        const error = await response.json();
        alert(`Failed to save recipe: ${error.detail}`);
      }
    } catch (error) {
      alert("An error occurred while saving the recipe.");
    }
  };


  if (data && Object.keys(data).length > 0) {
    if (showRecipe) {
      return (
        <div className="text-center">
          {renderRecipe(data)}
          <div className="flex gap-4 justify-center mt-8">
             <button onClick={() => setShowRecipe(false)} className="bg-gray-200 text-gray-800 font-bold py-3 px-6 rounded-lg hover:bg-gray-300 transition-colors">Back to Options</button>
          </div>
        </div>
      );
    }

    return (
      <div className="text-center p-6 bg-green-50 border border-green-200 rounded-lg">
        <CheckCircleIcon className="h-16 w-16 text-green-500 mx-auto mb-4" />
        <h3 className="text-2xl font-bold text-gray-800">Recipe Ready!</h3>
        <p className="text-gray-600 my-4">Your recipe for "{data.title}" has been generated.</p>

        <div className="grid grid-cols-2 gap-4 mt-8">
          <button onClick={() => alert("PDF generation coming soon!")} className="flex flex-col items-center justify-center gap-2 bg-white border border-gray-300 text-gray-700 font-semibold py-4 px-4 rounded-lg hover:bg-gray-100 transition-colors shadow-sm">
            <ArrowDownTrayIcon className="h-6 w-6 text-green-600" />
            <span>Save as PDF</span>
          </button>
          <button onClick={() => setShowRecipe(true)} className="flex flex-col items-center justify-center gap-2 bg-white border border-gray-300 text-gray-700 font-semibold py-4 px-4 rounded-lg hover:bg-gray-100 transition-colors shadow-sm">
            <BookOpenIcon className="h-6 w-6 text-blue-600" />
            <span>Show Recipe</span>
          </button>
          {currentUser && (
            <button onClick={handleSaveRecipe} className="flex flex-col items-center justify-center gap-2 bg-white border border-gray-300 text-gray-700 font-semibold py-4 px-4 rounded-lg hover:bg-gray-100 transition-colors shadow-sm">
              <BookmarkIcon className="h-6 w-6 text-purple-600" />
              <span>Save Recipe</span>
            </button>
          )}
          <button onClick={onReset} className="flex flex-col items-center justify-center gap-2 bg-white border border-gray-300 text-gray-700 font-semibold py-4 px-4 rounded-lg hover:bg-gray-100 transition-colors shadow-sm">
            <PlusCircleIcon className="h-6 w-6 text-gray-500" />
            <span>Create New</span>
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="text-center">
      <div className="flex justify-center my-6">
        <Spinner size="h-10 w-10" color="text-green-600" />
      </div>
      <h3 className="text-xl font-semibold text-gray-700">Generating your recipe...</h3>
      <p className="text-gray-500 my-4">{status}</p>
    </div>
  );
};


const Header = ({ currentUser, handleLogout, showAuthModal, usageStatus, showTestPdfModal }) => (
  <header className="bg-white/80 backdrop-blur-sm border-b border-gray-200/80 shadow-sm sticky top-0 z-50 font-poppins">
    <div className="max-w-7xl mx-auto px-6">
      <div className="flex justify-between items-center py-3">
        <div className="flex items-center gap-2">
          <a href="/">
            <img className="h-10 w-auto" src="/logo.png" alt="Food2Guide" />
          </a>
        </div>
        {currentUser ? (
          <div className="flex items-center gap-6">
            <div className="flex items-center gap-2">
                <UserCircleIcon className="h-6 w-6 text-gray-500" />
                <span className="text-sm text-gray-700 font-medium hidden sm:block">{currentUser.full_name || currentUser.email}</span>
            </div>
            {currentUser.is_admin && (
              <button 
                onClick={showTestPdfModal}
                className="flex items-center gap-2 text-sm text-blue-600 hover:text-blue-800 transition-colors"
              >
                <WrenchScrewdriverIcon className="h-5 w-5" />
                <span className="hidden sm:block">TEST PDF</span>
              </button>
            )}
            <button onClick={handleLogout} className="flex items-center gap-2 text-sm text-gray-600 hover:text-red-600 transition-colors">
              <ArrowRightOnRectangleIcon className="h-5 w-5" />
              <span className="hidden sm:block">Sign Out</span>
            </button>
          </div>
        ) : (
          <div className="flex items-center gap-4">
            {usageStatus && !usageStatus.is_authenticated && (
              <div className="text-sm text-gray-600">
                <span className="font-medium text-blue-600">{usageStatus.remaining_usage}</span> free PDFs remaining today
              </div>
            )}
            <button 
              onClick={showAuthModal} 
              className="flex items-center justify-center gap-2 py-2 px-4 border border-gray-300 rounded-lg shadow-sm hover:shadow-md bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 transition-all"
            >
                Sign In
            </button>
          </div>
        )}
      </div>
    </div>
  </header>
);

const AuthModal = ({ isOpen, onClose, handleLogin, handleRegister, initiateGoogleSignIn, authTab, setAuthTab }) => {
  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-2xl shadow-xl max-w-md w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-bold text-gray-800">Welcome to Food2Guide!</h2>
            <button 
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600 transition-colors"
            >
              <XMarkIcon className="h-6 w-6" />
            </button>
          </div>

          <div className="flex justify-center gap-2 bg-gray-100 p-1 rounded-xl shadow-inner mb-6">
            <button 
              onClick={() => setAuthTab('login')} 
              className={`flex-1 px-4 py-2 text-sm font-semibold rounded-lg transition-colors ${
                authTab === 'login' ? 'bg-white text-green-700 shadow-sm' : 'text-gray-500 hover:bg-gray-200'
              }`}
            >
              Login
            </button>
            <button 
              onClick={() => setAuthTab('register')} 
              className={`flex-1 px-4 py-2 text-sm font-semibold rounded-lg transition-colors ${
                authTab === 'register' ? 'bg-white text-green-700 shadow-sm' : 'text-gray-500 hover:bg-gray-200'
              }`}
            >
              Register
            </button>
          </div>

          {authTab === 'login' ? (
            <div className="space-y-6">
              <form onSubmit={handleLogin} className="space-y-4">
                <div>
                  <label htmlFor="login-email" className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                  <input 
                    type="email" 
                    name="email" 
                    id="login-email" 
                    required 
                    className="w-full px-4 py-2.5 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500" 
                  />
                </div>
                <div>
                  <label htmlFor="login-password" className="block text-sm font-medium text-gray-700 mb-1">Password</label>
                  <input 
                    type="password" 
                    name="password" 
                    id="login-password" 
                    required 
                    className="w-full px-4 py-2.5 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500" 
                  />
                </div>
                <button 
                  type="submit" 
                  className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-3 px-4 rounded-xl shadow-md hover:shadow-lg transition-transform transform hover:scale-[1.02]"
                >
                  Sign In
                </button>
              </form>

              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <div className="w-full border-t border-gray-300"></div>
                </div>
                <div className="relative flex justify-center text-sm">
                  <span className="px-2 bg-white text-gray-500">or</span>
                </div>
              </div>

              <button 
                onClick={initiateGoogleSignIn}
                className="w-full flex items-center justify-center gap-3 py-3 px-4 border border-gray-300 rounded-xl shadow-sm hover:shadow-md bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 transition-all"
              >
                <svg className="h-5 w-5" viewBox="0 0 24 24">
                  <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                  <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                  <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                  <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
                </svg>
                Continue with Google
              </button>
            </div>
          ) : (
            <div className="space-y-6">
              <form onSubmit={handleRegister} className="space-y-4">
                <div>
                  <label htmlFor="register-name" className="block text-sm font-medium text-gray-700 mb-1">Full Name</label>
                  <input 
                    type="text" 
                    name="name" 
                    id="register-name" 
                    required 
                    className="w-full px-4 py-2.5 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500" 
                  />
                </div>
                <div>
                  <label htmlFor="register-email" className="block text-sm font-medium text-gray-700 mb-1">Email</label>
                  <input 
                    type="email" 
                    name="email" 
                    id="register-email" 
                    required 
                    className="w-full px-4 py-2.5 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500" 
                  />
                </div>
                <div>
                  <label htmlFor="register-password" className="block text-sm font-medium text-gray-700 mb-1">Password</label>
                  <input 
                    type="password" 
                    name="password" 
                    id="register-password" 
                    required 
                    minLength="6"
                    className="w-full px-4 py-2.5 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 focus:border-green-500" 
                  />
                </div>
                <button 
                  type="submit" 
                  className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-3 px-4 rounded-xl shadow-md hover:shadow-lg transition-transform transform hover:scale-[1.02]"
                >
                  Create Account
                </button>
              </form>

              <div className="relative">
                <div className="absolute inset-0 flex items-center">
                  <div className="w-full border-t border-gray-300"></div>
                </div>
                <div className="relative flex justify-center text-sm">
                  <span className="px-2 bg-white text-gray-500">or</span>
                </div>
              </div>

              <button 
                onClick={initiateGoogleSignIn}
                className="w-full flex items-center justify-center gap-3 py-3 px-4 border border-gray-300 rounded-xl shadow-sm hover:shadow-md bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 transition-all"
              >
                <svg className="h-5 w-5" viewBox="0 0 24 24">
                  <path fill="#4285F4" d="M22.56 12.25c0-.78-.07-1.53-.2-2.25H12v4.26h5.92c-.26 1.37-1.04 2.53-2.21 3.31v2.77h3.57c2.08-1.92 3.28-4.74 3.28-8.09z"/>
                  <path fill="#34A853" d="M12 23c2.97 0 5.46-.98 7.28-2.66l-3.57-2.77c-.98.66-2.23 1.06-3.71 1.06-2.86 0-5.29-1.93-6.16-4.53H2.18v2.84C3.99 20.53 7.7 23 12 23z"/>
                  <path fill="#FBBC05" d="M5.84 14.09c-.22-.66-.35-1.36-.35-2.09s.13-1.43.35-2.09V7.07H2.18C1.43 8.55 1 10.22 1 12s.43 3.45 1.18 4.93l2.85-2.22.81-.62z"/>
                  <path fill="#EA4335" d="M12 5.38c1.62 0 3.06.56 4.21 1.64l3.15-3.15C17.45 2.09 14.97 1 12 1 7.7 1 3.99 3.47 2.18 7.07l3.66 2.84c.87-2.6 3.3-4.53 6.16-4.53z"/>
                </svg>
                Sign up with Google
              </button>
            </div>
          )}


        </div>
      </div>
    </div>
  );
};

const TestPdfModal = ({ isOpen, onClose, currentUser }) => {
  const [templates, setTemplates] = useState([]);
  const [selectedTemplate, setSelectedTemplate] = useState('professional');
  const [selectedOrientation, setSelectedOrientation] = useState('landscape');
  const [isGenerating, setIsGenerating] = useState(false);

  // Fetch available templates when modal opens
  useEffect(() => {
    if (isOpen && currentUser?.is_admin) {
      fetchTemplates();
    }
  }, [isOpen, currentUser]);

  const fetchTemplates = async () => {
    try {
      const response = await fetch(`${API_BASE}/api/v1/templates`, {
        credentials: 'include'
      });
      if (response.ok) {
        const data = await response.json();
        setTemplates(data.templates || []);
      }
    } catch (error) {
      console.error('Error fetching templates:', error);
    }
  };

  const generateTestPdf = async () => {
    setIsGenerating(true);
    try {
      const formData = new FormData();
      formData.append('template_name', selectedTemplate);
      formData.append('image_orientation', selectedOrientation);

      const response = await fetch(`${API_BASE}/api/v1/test-pdf`, {
        method: 'POST',
        credentials: 'include',
        body: formData
      });

      if (response.ok) {
        // Download the PDF
        const blob = await response.blob();
        const url = window.URL.createObjectURL(blob);
        const a = document.createElement('a');
        a.href = url;
        a.download = `test-${selectedTemplate}-${selectedOrientation}.pdf`;
        document.body.appendChild(a);
        a.click();
        window.URL.revokeObjectURL(url);
        document.body.removeChild(a);

        // Close modal
        onClose();
      } else {
        const error = await response.json();
        alert(`Error: ${error.detail || 'Failed to generate test PDF'}`);
      }
    } catch (error) {
      console.error('Error generating test PDF:', error);
      alert('Error generating test PDF. Please try again.');
    } finally {
      setIsGenerating(false);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-2xl shadow-xl max-w-md w-full max-h-[90vh] overflow-y-auto">
        <div className="p-6">
          <div className="flex justify-between items-center mb-6">
            <h2 className="text-2xl font-bold text-gray-800">Test PDF Generation</h2>
            <button 
              onClick={onClose}
              className="text-gray-400 hover:text-gray-600 transition-colors"
            >
              <XMarkIcon className="h-6 w-6" />
            </button>
          </div>

          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                CSS Template
              </label>
              <select 
                value={selectedTemplate}
                onChange={(e) => setSelectedTemplate(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                {templates.map(template => (
                  <option key={template} value={template}>
                    {template.charAt(0).toUpperCase() + template.slice(1)}
                  </option>
                ))}
              </select>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Image Orientation
              </label>
              <select 
                value={selectedOrientation}
                onChange={(e) => setSelectedOrientation(e.target.value)}
                className="w-full px-3 py-2 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="landscape">Landscape (YouTube)</option>
                <option value="portrait">Portrait (TikTok/Shorts)</option>
              </select>
            </div>

            <div className="pt-4">
              <button
                onClick={generateTestPdf}
                disabled={isGenerating}
                className={`w-full font-bold py-3 px-4 rounded-xl shadow-md hover:shadow-lg transition-transform transform hover:scale-[1.02] ${
                  isGenerating 
                    ? 'bg-gray-400 cursor-not-allowed' 
                    : 'bg-blue-600 hover:bg-blue-700 text-white'
                }`}
              >
                {isGenerating ? 'Generating...' : 'CREATE'}
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

const JobStatus = ({ job, onReset }) => {
  const [status, setStatus] = useState(job.status);
  const [details, setDetails] = useState(job.details);
  const [pdfUrl, setPdfUrl] = useState(null);
  const [error, setError] = useState(null);

  const statusStates = [
    { key: 'downloading', text: 'Downloading' },
    { key: 'transcribing', text: 'Transcribing' },
    { key: 'analyzing', text: 'Analyzing' },
    { key: 'extracting_frames', text: 'Extracting Frames' },
    { key: 'generating_pdf', text: 'Generating PDF' },
  ];
  const currentStatusIndex = statusStates.findIndex(s => status.toLowerCase().includes(s.key));

  useEffect(() => {
    if (status === 'completed' || status === 'failed') {
      if (job.pdf_url) setPdfUrl(job.pdf_url);
      return;
    }

    const interval = setInterval(async () => {
      try {
        const response = await fetch(`${API_BASE}/api/v1/status/${job.job_id}`, { credentials: 'include' });
        if (!response.ok) throw new Error('Network response was not ok');

        const data = await response.json();
        setStatus(data.status);
        setDetails(data.details);

        if (data.status === 'completed') {
          setPdfUrl(data.pdf_url);
          clearInterval(interval);
        } else if (data.status === 'failed') {
          setError(data.details);
          clearInterval(interval);
        }
      } catch (err) {
        setError('Failed to fetch status.');
        clearInterval(interval);
      }
    }, 2000);

    return () => clearInterval(interval);
  }, [job.job_id, status, job.pdf_url]);

  if (status === 'failed') {
    return (
      <div className="text-center">
        <h3 className="text-xl font-semibold text-red-600">Generation Failed</h3>
        <p className="text-gray-600 my-4">{error || details}</p>
        <button onClick={onReset} className="bg-gray-700 text-white font-bold py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors">Start Over</button>
      </div>
    );
  }

  if (status === 'completed' && pdfUrl) {
    return (
      <div className="text-center">
        <h3 className="text-xl font-semibold text-green-600">Recipe Ready!</h3>
        <p className="text-gray-600 my-4">Your recipe has been generated successfully.</p>
        <div className="flex gap-4 justify-center">
          <a href={`${API_BASE}${pdfUrl}`} target="_blank" rel="noopener noreferrer" className="inline-flex items-center gap-2 bg-green-600 text-white font-bold py-3 px-6 rounded-lg hover:bg-green-700 transition-colors">
            <ArrowDownTrayIcon className="h-5 w-5" /> Download PDF
          </a>
          <button onClick={onReset} className="bg-gray-200 text-gray-800 font-bold py-3 px-6 rounded-lg hover:bg-gray-300 transition-colors">Create Another</button>
        </div>
      </div>
    );
  }

  // Determine current step based on status
  const getCurrentStep = () => {
    if (status.toLowerCase().includes('downloading') || status.toLowerCase().includes('processing')) return 1;
    if (status.toLowerCase().includes('transcribing')) return 2;
    if (status.toLowerCase().includes('analyzing')) return 3;
    if (status.toLowerCase().includes('frames') || status.toLowerCase().includes('extracting') || status.toLowerCase().includes('generating') || status.toLowerCase().includes('pdf')) return 4;
    return 1; // Default to step 1
  };

  const currentStep = getCurrentStep();
  const totalSteps = 4;

  return (
    <div className="text-center">
      <div className="mb-4">
        <span className="inline-block bg-blue-100 text-blue-800 text-sm font-medium px-3 py-1 rounded-full">
          Step {currentStep}/{totalSteps}
        </span>
      </div>
      <h3 className="text-xl font-semibold text-blue-600">Processing Video</h3>
      <p className="text-gray-600 my-4">{details}</p>
      <div className="flex justify-center my-6">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
      <button onClick={onReset} className="bg-gray-200 text-gray-700 font-semibold py-2 px-4 rounded-lg hover:bg-gray-300">Start Over</button>
    </div>
  );
};


export default function Food2Guide() {
