  const [showAdvancedSettings, setShowAdvancedSettings] = useState(false);
  const [showImages, setShowImages] = useState(true); // Yes (default)
  const [dietPreference, setDietPreference] = useState('regular'); // regular, vegetarian, vegan
  const [allergies, setAllergies] = useState({ gluten: false, nuts: false, eggs: false, dairy: false, shellfish: false });
  const [instructionLevel, setInstructionLevel] = useState('intermediate'); // beginner, intermediate (default), expert
  const [showCalories, setShowCalories] = useState(false); // No (default)
  const [pdfStyle, setPdfStyle] = useState('modern');
  const [includeNutrition, setIncludeNutrition] = useState(true);
  const [includeTips, setIncludeTips] = useState(true);
  const [maxSteps, setMaxSteps] = useState(10);
  const [imageQuality, setImageQuality] = useState('high');
  const [videoSource, setVideoSource] = useState('youtube'); // youtube eller tiktok
  const [searchAttempted, setSearchAttempted] = useState(false); // New state to track if a search has been made
  const [isProcessing, setIsProcessing] = useState(false);
  const [showAuthModal, setShowAuthModal] = useState(false);

  // States for streaming
  const [isStreaming, setIsStreaming] = useState(false);
  const [recipeData, setRecipeData] = useState({});
  const [streamError, setStreamError] = useState(null);
  const [streamStatus, setStreamStatus] = useState('');

  const languages = [
    { code: 'en', name: 'English' },
    { code: 'sv', name: 'Svenska' },
    { code: 'no', name: 'Norsk' },
    { code: 'da', name: 'Dansk' },
    { code: 'de', name: 'Deutsch' },
    { code: 'fr', name: 'Franais' },
    { code: 'es', name: 'Espaol' },
  ];

  useEffect(() => {
    // Check auth status on initial load to see if a cookie session exists
    checkAuthStatus();
    checkUsageStatus();

    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');
    if (code) {
        handleGoogleOAuthCallback(code);
        window.history.replaceState({}, document.title, window.location.pathname);
    }
  }, []); // Run only once on mount

  const checkAuthStatus = async () => {
    try {
      const response = await fetch(`${API_BASE}/api/v1/auth/me`, {
        credentials: 'include'
      });

      if (response.ok) {
        const user = await response.json();
        setCurrentUser(user);
      } else {
        setCurrentUser(null);
      }
    } catch (error) {
      console.error('Auth check error:', error);
      setCurrentUser(null);
    }
  };

  const checkUsageStatus = async () => {
    try {
      const response = await fetch(`${API_BASE}/api/v1/usage-status`, { credentials: 'include' });
      if (response.ok) {
        const status = await response.json();
        setUsageStatus(status);
      }
    } catch (error) {
      console.error('Usage status check error:', error);
    }
  };

  const handleAuthAction = async (url, body) => {
    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body)
      });

      const data = await response.json();

      if (response.ok) {
        setCurrentUser(data.user); // The user object is now returned directly
        setIsAuthModalOpen(false);
        setShowWelcome(true);
        setTimeout(() => setShowWelcome(false), 4000);
      } else {
        alert(data.detail || 'Action failed');
      }
    } catch (error) {
      alert('An error occurred. Please try again.');
    }
  };

  const handleLogin = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const email = formData.get('email');
    const password = formData.get('password');

    // Use regular login endpoint for both admin and regular users
    const body = { email, password };
    handleAuthAction(`${API_BASE}/api/v1/auth/login`, body);
  };

  const handleRegister = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const body = {
      full_name: formData.get('name'),
      email: formData.get('email'),
      password: formData.get('password')
    };
    handleAuthAction(`${API_BASE}/api/v1/auth/register`, body);
  };

  const handleLogout = async () => {
    try {
      await fetch(`${API_BASE}/api/v1/auth/logout`, { method: 'POST', credentials: 'include' });
    } catch (error) {
      console.error("Logout failed", error);
    } finally {
      setCurrentUser(null);
    }
  };

  const initiateGoogleSignIn = async () => {
    try {
      const urlResponse = await fetch(`${API_BASE}/api/v1/auth/google/url`, { credentials: 'include' });
      if (!urlResponse.ok) throw new Error('Failed to get Google OAuth URL');
      const urlData = await urlResponse.json();
      if (!urlData.auth_url) {
          alert('Google Sign-In is not available.');
        return;
      }
      setIsAuthModalOpen(false); // Close modal before redirecting
      window.location.href = urlData.auth_url;
    } catch (error) {
      console.error('Google Sign-In error:', error);
      alert('Could not initiate Google Sign-In.');
    }
  };

  const handleGoogleOAuthCallback = async (code) => {
    try {
      const authResponse = await fetch(`${API_BASE}/api/v1/auth/google/callback`, {
        method: 'POST',
        credentials: 'include',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ code })
      });
      if (authResponse.ok) {
        // The cookie is set automatically by the browser. We just need to check status again.
        checkAuthStatus();
        setIsAuthModalOpen(false);
        setShowWelcome(true);
        setTimeout(() => setShowWelcome(false), 4000);
      } else {
        const error = await authResponse.json();
        alert(error.detail || 'Google Sign-In failed');
      }
    } catch (error) {
      console.error('Google OAuth callback error:', error);
        alert('Google Sign-In failed.');
    }
  };

  const handleScroll = () => {
    const container = scrollContainerRef.current;
    if (container) {
      if (container.scrollTop + container.clientHeight >= container.scrollHeight - 100) {
        loadMoreResults();
      }
    }
  };

  const loadMoreResults = async () => {
    if (isSearching || !hasMoreResults) return;

    setIsSearching(true);
    try {
      const response = await fetch(`${API_BASE}/api/v1/search`, {
        method: 'POST',
        credentials: 'include',
        headers: { 
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({ query: searchQuery, language, source: videoSource, page: searchPage + 1 })
      });
      if (response.ok) {
        const data = await response.json();
        setSearchResults(prevResults => [...prevResults, ...data.results]);
        setSearchPage(prevPage => prevPage + 1);
        if (data.results.length < 10) {
          setHasMoreResults(false);
        }
      } else {
        setHasMoreResults(false);
      }
    } catch (error) {
      setHasMoreResults(false);
    } finally {
      setIsSearching(false);
    }
  };

  // ndra searchYouTube s att den skickar med videoSource
  const searchYouTube = async () => {
    if (!searchQuery.trim()) return;
    setIsSearching(true);
    setSearchAttempted(true); // Mark that a search has been attempted
    setSearchResults([]); // Clear previous results
    setSearchPage(1);
    setHasMoreResults(true);
    try {
      const response = await fetch(`${API_BASE}/api/v1/search`, {
        method: 'POST',
        credentials: 'include',
        headers: { 
            'Content-Type': 'application/json'
        },
        body: JSON.stringify({ query: searchQuery, language, source: videoSource, page: 1 })
      });
      if (response.ok) {
        const data = await response.json();
        setSearchResults(data.results);
        if (data.results.length < 10) {
          setHasMoreResults(false);
        }
      } else {
        alert('Search failed.');
        setHasMoreResults(false);
      }
    } catch (error) {
      alert('An error occurred during search.');
      setHasMoreResults(false);
    } finally {
      setIsSearching(false);
    }
  };

  const processVideo = async () => {
    if (!videoUrl) {
      alert('Please provide a video URL.');
      return;
    }

    setIsStreaming(true);
    setRecipeData({});
    setStreamError(null);
    setStreamStatus('Initializing stream...');

    const url = `${API_BASE}/api/v1/generate-stream?video_url=${encodeURIComponent(videoUrl)}&language=${language}`;

    try {
      const response = await fetch(url, {
        method: 'GET',
        headers: {
          'Accept': 'text/event-stream'
        },
        credentials: 'include' // This is the crucial part for sending cookies
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let buffer = '';

      const processStream = async () => {
        while (true) {
          const { done, value } = await reader.read();
          if (done) {
            setStreamStatus('Recipe generation complete!');
            setIsStreaming(false);
            break;
          }

          buffer += decoder.decode(value, { stream: true });
          const lines = buffer.split('\n');
          buffer = lines.pop(); // Keep the last, potentially incomplete line

          for (const line of lines) {
            if (line.startsWith('data: ')) {
              try {
                const jsonStr = line.substring(6);
                const data = JSON.parse(jsonStr);

                if (data.status === 'error') {
                  setStreamError(data.message);
                  setStreamStatus('Error occurred.');
                  reader.cancel();
                  return;
                } else if (data.status) {
                  setStreamStatus(data.message);
                } else {
                  setRecipeData(prevData => {
                    const newData = { ...prevData };
                    for (const key in data) {
                      if (Array.isArray(data[key]) && Array.isArray(newData[key])) {
                        newData[key] = [...newData[key], ...data[key]];
                      } else {
                        newData[key] = data[key];
                      }
                    }
                    return newData;
                  });
                }
              } catch (e) {
                console.error('Failed to parse stream data:', e);
              }
            }
          }
        }
      };

      processStream();

    } catch (error) {
      console.error('EventSource failed:', error);
      setStreamError('Failed to connect to the server. Please try again.');
      setStreamStatus('Connection error.');
      setIsStreaming(false);
