import React, { useState, useEffect, useRef } from 'react';
import { 
  LinkIcon, 
  MagnifyingGlassIcon, 
  CheckCircleIcon,
  ArrowDownTrayIcon,
  ArrowRightOnRectangleIcon,
  UserCircleIcon,
  SparklesIcon,
  FireIcon,
  XMarkIcon,
  WrenchScrewdriverIcon,
} from '@heroicons/react/24/outline';

const API_BASE = 'http://localhost:8000';

const Spinner = ({ size = 'h-5 w-5', color = 'text-white' }) => (
  <svg className={`animate-spin ${size} ${color}`} xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
  </svg>
);

const Header = ({ currentUser, handleLogout, showAuthModal, usageStatus, showTestPdfModal }) => (
    <header className="bg-white/80 backdrop-blur-sm border-b border-gray-200/80 shadow-sm sticky top-0 z-50 font-poppins">
      <div className="max-w-7xl mx-auto px-6">
        <div className="flex justify-between items-center py-3">
          <div className="flex items-center gap-2">
            <a href="/">
              <img className="h-10 w-auto" src="/logo.png" alt="Food2Guide" />
            </a>
          </div>
          {currentUser ? (
            <div className="flex items-center gap-6">
              <div className="flex items-center gap-2">
                  <UserCircleIcon className="h-6 w-6 text-gray-500" />
                  <span className="text-sm text-gray-700 font-medium hidden sm:block">{currentUser.full_name || currentUser.email}</span>
              </div>
              {currentUser.is_admin && (
                <button 
                  onClick={showTestPdfModal}
                  className="flex items-center gap-2 text-sm text-blue-600 hover:text-blue-800 transition-colors"
                >
                  <WrenchScrewdriverIcon className="h-5 w-5" />
                  <span className="hidden sm:block">TEST PDF</span>
                </button>
              )}
              <button onClick={handleLogout} className="flex items-center gap-2 text-sm text-gray-600 hover:text-red-600 transition-colors">
                <ArrowRightOnRectangleIcon className="h-5 w-5" />
                <span className="hidden sm:block">Sign Out</span>
              </button>
            </div>
          ) : (
            <div className="flex items-center gap-4">
              {usageStatus && !usageStatus.is_authenticated && (
                <div className="text-sm text-gray-600">
                  <span className="font-medium text-blue-600">{usageStatus.remaining_usage}</span> free PDFs remaining today
                </div>
              )}
              <button 
                onClick={showAuthModal} 
                className="flex items-center justify-center gap-2 py-2 px-4 border border-gray-300 rounded-lg shadow-sm hover:shadow-md bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 transition-all"
              >
                  Sign In
              </button>
            </div>
          )}
        </div>
      </div>
    </header>
  );

  const RecipeStreamViewer = ({ status, error, data, onReset }) => {
    // This is a "dumb" component. It just renders the props it's given.
    // The parent component is responsible for building the data object over time.

    if (error) {
      return (
        <div className="bg-white p-8 rounded-2xl shadow-lg animate-fade-in text-center">
          <h3 className="text-xl font-semibold text-red-600">Generation Failed</h3>
          <p className="text-gray-600 my-4">{error}</p>
          <button onClick={onReset} className="bg-gray-700 text-white font-bold py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors">Start Over</button>
        </div>
      );
    }

    const handleDownload = () => {
      alert("PDF generation from the streamed recipe is not yet implemented in this view.");
    };

    if (status === 'completed' && data) {
      return (
          <div className="bg-white p-8 rounded-2xl shadow-lg animate-fade-in">
            <div className="text-left mb-8">
              <h2 className="text-3xl font-bold text-gray-800 mb-4">{data.title || "Recipe"}</h2>
              <p className="text-gray-600 mb-6">{data.description}</p>

              <div className="grid md:grid-cols-3 gap-8">
                <div className="md:col-span-1">
                  <h4 className="text-xl font-bold text-gray-800 mb-4 border-b-2 border-green-400 pb-2">Ingredients</h4>
                  <ul className="list-disc list-inside text-gray-700 space-y-2">
                    {data.ingredients?.map((ing, i) => (
                      <li key={i}>
                        <span className="font-semibold">{ing.quantity}</span> {ing.name}
                      </li>
                    ))}
                  </ul>
                </div>
                <div className="md:col-span-2">
                  <h4 className="text-xl font-bold text-gray-800 mb-4 border-b-2 border-green-400 pb-2">Instructions</h4>
                  <ol className="list-decimal list-inside text-gray-700 space-y-4">
                    {data.instructions?.map((instr, i) => (
                      <li key={i}>{instr}</li>
                    ))}
                  </ol>
                </div>
              </div>
            </div>
            <div className="flex gap-4 justify-center mt-8">
              <button onClick={handleDownload} className="inline-flex items-center gap-2 bg-green-600 text-white font-bold py-3 px-6 rounded-lg hover:bg-green-700 transition-colors">
                <ArrowDownTrayIcon className="h-5 w-5" /> Download PDF
              </button>
              <button onClick={onReset} className="bg-gray-200 text-gray-800 font-bold py-3 px-6 rounded-lg hover:bg-gray-300 transition-colors">Create Another</button>
            </div>
          </div>
      );
    }

    // Streaming view
    return (
      <div className="bg-white p-8 rounded-2xl shadow-lg animate-fade-in">
        <h2 className="text-2xl font-bold text-center text-gray-800 mb-4">{data?.title || "Generating Recipe..."}</h2>
        <p className="text-center text-gray-500 mb-6">{status}</p>

        {data ? (
          <div className="space-y-6 animate-fade-in">
            {data.description && <p className="text-gray-600 text-center mb-6">{data.description}</p>}

            <div className="grid md:grid-cols-3 gap-8">
              {data.ingredients && (
                <div className="md:col-span-1">
                  <h4 className="text-xl font-bold text-gray-800 mb-4 border-b-2 border-green-400 pb-2">Ingredients</h4>
                  <ul className="list-disc list-inside text-gray-700 space-y-2">
                    {data.ingredients.map((ing, i) => (
                      <li key={i}><span className="font-semibold">{ing.quantity}</span> {ing.name}</li>
                    ))}
                  </ul>
                </div>
              )}
              {data.instructions && (
                <div className="md:col-span-2">
                  <h4 className="text-xl font-bold text-gray-800 mb-4 border-b-2 border-green-400 pb-2">Instructions</h4>
                  <ol className="list-decimal list-inside text-gray-700 space-y-4">
                    {data.instructions.map((instr, i) => <li key={i}>{instr}</li>)}
                  </ol>
                </div>
              )}
            </div>
          </div>
        ) : (
           <div className="flex justify-center items-center p-8">
              <Spinner size="h-8 w-8" color="text-green-600" />
           </div>
        )}
      </div>
    );
  };

  export default function Food2Guide() {
    const [videoUrl, setVideoUrl] = useState('');
    const [language, setLanguage] = useState('en');
    const [authToken, setAuthToken] = useState(null);

    const [isStreaming, setIsStreaming] = useState(false);
    const [streamStatus, setStreamStatus] = useState(''); // Holds text like "Transcribing...", "Generating..."
    const [streamError, setStreamError] = useState(null);
    const [recipeData, setRecipeData] = useState(null); // Holds the structured recipe object

    const languages = [
      { code: 'en', name: 'English' },
      { code: 'sv', name: 'Svenska' },
    ];

    const resetAll = () => {
      setVideoUrl('');
      setIsStreaming(false);
      setRecipeData(null);
      setStreamError(null);
      setStreamStatus('');
    };

    const processVideo = async () => {
      if (!videoUrl) {
        alert('Please provide a video URL.');
        return;
      }
      resetAll();
      setIsStreaming(true);
      setStreamStatus('Initializing...');

      try {
        const response = await fetch(`${API_BASE}/api/v1/generate-stream`, {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({ youtube_url: videoUrl, language: language }),
        });

        if (!response.ok || !response.body) {
          throw new Error('Failed to start the recipe generation stream.');
        }

        const reader = response.body.getReader();
        const decoder = new TextDecoder();
        let buffer = "";

        const processStream = async () => {
          while (true) {
            const { done, value } = await reader.read();
            if (done) {
              setStreamStatus(prev => (prev === 'completed' ? prev : 'completed'));
              break;
            }

            buffer += decoder.decode(value, { stream: true });
            const lines = buffer.split('\n');
            buffer = lines.pop(); // Keep the last partial line in the buffer

            for (const line of lines) {
              if (line.startsWith('data: ')) {
                try {
                  const jsonStr = line.substring(6);
                  const eventData = JSON.parse(jsonStr);

                  if (eventData.status) {
                    if (eventData.status === 'error') {
                      setStreamError(eventData.message);
                      setIsStreaming(false);
                      return; // Stop processing on error
                    }
                    if (eventData.status !== 'completed') {
                      setStreamStatus(eventData.message);
                    } else {
                      setStreamStatus('completed');
                    }
                  } else if (eventData.content) {
                    setRecipeData(prevData => {
                      const newData = { ...prevData };
                      try {
                        const partial = JSON.parse(eventData.content);
                        // A very basic merge. A more robust solution would handle nested objects.
                        for (const key in partial) {
                          newData[key] = partial[key];
                        }
                      } catch (e) {
                        // Incomplete JSON, we just append the string content for now
                        // A better approach is needed for robust partial JSON parsing.
                      }
                      return newData;
                    });
                  }
                } catch (e) {
                  // Incomplete JSON, just continue
                }
              }
            }
          }
        };

        await processStream();

      } catch (err) {
        setStreamError(err.message || "An unknown error occurred.");
        setIsStreaming(false);
      }
    };

    return (
      <div className="font-poppins bg-gradient-to-br from-[#d6e5dd] to-[#eaf3ef] min-h-screen">
        <Header currentUser={null} />
        <main className="max-w-2xl mx-auto py-12 px-6">
          <div className="relative bg-white shadow-lg rounded-2xl p-8">
            <SparklesIcon className="absolute top-0 right-0 h-24 w-24 text-yellow-300/50 -translate-y-1/4 translate-x-1/4" />

            {isStreaming ? (
              <RecipeStreamViewer status={streamStatus} error={streamError} data={recipeData} onReset={resetAll} />
            ) : (
              <>
                <h1 className="text-3xl font-bold tracking-tight text-gray-900 text-center">
                  Turn Cooking Videos into Recipes
                </h1>
                <p className="mt-4 text-center text-gray-600">
                  Paste a link to a YouTube or TikTok cooking video and get a step-by-step recipe in seconds.
                </p>

                <div className="mt-8 space-y-6">
                   <div>
                      <div className="relative">
                        <LinkIcon className="absolute left-3 top-1/2 -translate-y-1/2 h-5 w-5 text-gray-400" />
                        <input
                          id="video-url"
                          type="text"
                          value={videoUrl}
                          onChange={(e) => setVideoUrl(e.target.value)}
                          placeholder="Paste a YouTube or TikTok recipe video URL"
                          className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500"
                        />
                      </div>
                   </div>
                  <div>
                    <select
                      id="language-select"
                      value={language}
                      onChange={(e) => setLanguage(e.target.value)}
                      className="w-full px-4 py-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 bg-white"
                    >
                      {languages.map(lang => <option key={lang.code} value={lang.code}>{lang.name}</option>)}
                    </select>
                  </div>

                  <div className="pt-2">
                    <button onClick={processVideo} className="flex items-center gap-2 w-full justify-center bg-green-600 hover:bg-green-700 text-white font-bold rounded-xl shadow-md px-6 py-4 text-lg transition-transform hover:scale-105">
                      <SparklesIcon className="h-6 w-6" />
                      <span>Generate Recipe</span>
                    </button>
                  </div>
                </div>
              </>
            )}
          </div>
        </main>
      </div>
    );
  }
