import React, { useState, useEffect, useRef } from 'react';
import { 
  LinkIcon, 
  MagnifyingGlassIcon,
  CheckCircleIcon,
  ArrowDownTrayIcon,
  ArrowRightOnRectangleIcon,
  UserCircleIcon,
  SparklesIcon,
  FireIcon,
} from '@heroicons/react/24/outline';

const API_BASE = 'http://localhost:8000';

const Header = ({ currentUser, handleLogout, initiateGoogleSignIn }) => (
  <header className="bg-white/80 backdrop-blur-sm border-b border-gray-200/80 shadow-sm sticky top-0 z-50 font-poppins">
    <div className="max-w-7xl mx-auto px-6">
      <div className="flex justify-between items-center py-4">
        <div className="flex items-center gap-3">
          <img className="h-9 w-auto" src="/logo.png" alt="Food2Guide" />
        </div>
        {currentUser ? (
          <div className="flex items-center gap-6">
            <div className="flex items-center gap-2">
                <UserCircleIcon className="h-6 w-6 text-gray-500" />
                <span className="text-sm text-gray-700 font-medium hidden sm:block">Welcome, {currentUser.full_name || currentUser.email}</span>
            </div>
            <button onClick={handleLogout} className="flex items-center gap-2 text-sm text-gray-600 hover:text-red-600 transition-colors">
              <ArrowRightOnRectangleIcon className="h-5 w-5" />
              <span className="hidden sm:block">Sign Out</span>
            </button>
          </div>
        ) : (
          <div className="flex items-center">
            <button 
              onClick={initiateGoogleSignIn} 
              className="flex items-center justify-center gap-2 py-2 px-4 border border-gray-300 rounded-lg shadow-sm hover:shadow-md bg-white text-sm font-medium text-gray-700 hover:bg-gray-50 transition-all"
            >
                <img className="h-4 w-4 mr-2" src="https://upload.wikimedia.org/wikipedia/commons/c/c1/Google_%22G%22_logo.svg" alt="Google logo" />
                Sign In with Google
            </button>
          </div>
        )}
      </div>
    </div>
  </header>
);

const AuthForm = ({ handleLogin, handleRegister, setAuthTab, authTab }) => (
    <div className="mt-8">
        <div className="flex justify-center gap-2 bg-gray-100 p-1 rounded-xl shadow-inner">
            <button onClick={() => setAuthTab('login')} className={`flex-1 px-4 py-2 text-sm font-semibold rounded-lg transition-colors ${authTab === 'login' ? 'bg-white text-green-700 shadow-sm' : 'text-gray-500 hover:bg-gray-200'}`}>Login</button>
            <button onClick={() => setAuthTab('register')} className={`flex-1 px-4 py-2 text-sm font-semibold rounded-lg transition-colors ${authTab === 'register' ? 'bg-white text-green-700 shadow-sm' : 'text-gray-500 hover:bg-gray-200'}`}>Register</button>
        </div>
        <div className="pt-8">
            {authTab === 'login' ? (
                <form onSubmit={handleLogin} className="space-y-6">
                    <div>
                        <label htmlFor="login-email" className="block text-sm font-medium text-gray-700">Email</label>
                        <input type="email" name="email" id="login-email" required className="mt-1 block w-full px-4 py-2.5 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500" />
                    </div>
                    <div>
                        <label htmlFor="login-password" className="block text-sm font-medium text-gray-700">Password</label>
                        <input type="password" name="password" id="login-password" required className="mt-1 block w-full px-4 py-2.5 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500" />
                    </div>
                    <button type="submit" className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-3 px-4 rounded-xl shadow-md hover:shadow-lg transition-transform transform hover:scale-[1.02]">Sign In</button>
                </form>
            ) : (
                 <form onSubmit={handleRegister} className="space-y-6">
                    <div>
                        <label htmlFor="register-name" className="block text-sm font-medium text-gray-700">Full Name</label>
                        <input type="text" name="name" id="register-name" required className="mt-1 block w-full px-4 py-2.5 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500" />
                    </div>
                    <div>
                        <label htmlFor="register-email" className="block text-sm font-medium text-gray-700">Email</label>
                        <input type="email" name="email" id="register-email" required className="mt-1 block w-full px-4 py-2.5 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500" />
                    </div>
                    <div>
                        <label htmlFor="register-password" className="block text-sm font-medium text-gray-700">Password</label>
                        <input type="password" name="password" id="register-password" required className="mt-1 block w-full px-4 py-2.5 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500" />
                    </div>
                    <button type="submit" className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-3 px-4 rounded-xl shadow-md hover:shadow-lg transition-transform transform hover:scale-[1.02]">Create Account</button>
                </form>
            )}
        </div>
    </div>
);

const JobStatus = ({ job, onReset }) => {
  const [status, setStatus] = useState(job.status);
  const [details, setDetails] = useState(job.details);
  const [pdfUrl, setPdfUrl] = useState(null);
  const [error, setError] = useState(null);

  const statusStates = [
    { key: 'downloading', text: 'Downloading' },
    { key: 'transcribing', text: 'Transcribing' },
    { key: 'analyzing', text: 'Analyzing' },
    { key: 'extracting_frames', text: 'Extracting Frames' },
    { key: 'generating_pdf', text: 'Generating PDF' },
  ];
  const currentStatusIndex = statusStates.findIndex(s => status.toLowerCase().includes(s.key));

  useEffect(() => {
    if (status === 'completed' || status === 'failed') {
      if (job.pdf_url) setPdfUrl(job.pdf_url);
      return;
    }

    const interval = setInterval(async () => {
      try {
        const response = await fetch(`${API_BASE}/api/v1/status/${job.job_id}`);
        if (!response.ok) throw new Error('Network response was not ok');

        const data = await response.json();
        setStatus(data.status);
        setDetails(data.details);

        if (data.status === 'completed') {
          setPdfUrl(data.pdf_url);
          clearInterval(interval);
        } else if (data.status === 'failed') {
          setError(data.details);
          clearInterval(interval);
        }
      } catch (err) {
        setError('Failed to fetch status.');
        clearInterval(interval);
      }
    }, 2000);

    return () => clearInterval(interval);
  }, [job.job_id, status, job.pdf_url]);

  if (status === 'failed') {
    return (
      <div className="text-center">
        <h3 className="text-xl font-semibold text-red-600">Generation Failed</h3>
        <p className="text-gray-600 my-4">{error || details}</p>
        <button onClick={onReset} className="bg-gray-700 text-white font-bold py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors">Start Over</button>
      </div>
    );
  }

  if (status === 'completed' && pdfUrl) {
    return (
      <div className="text-center">
        <h3 className="text-xl font-semibold text-green-600">Recipe Ready!</h3>
        <p className="text-gray-600 my-4">Your recipe has been generated successfully.</p>
        <div className="flex gap-4 justify-center">
          <a href={`${API_BASE}${pdfUrl}`} target="_blank" rel="noopener noreferrer" className="inline-flex items-center gap-2 bg-green-600 text-white font-bold py-3 px-6 rounded-lg hover:bg-green-700 transition-colors">
            <ArrowDownTrayIcon className="h-5 w-5" /> Download PDF
          </a>
          <button onClick={onReset} className="bg-gray-200 text-gray-800 font-bold py-3 px-6 rounded-lg hover:bg-gray-300 transition-colors">Create Another</button>
        </div>
      </div>
    );
  }

  // Determine current step based on status
  const getCurrentStep = () => {
    if (status.toLowerCase().includes('downloading') || status.toLowerCase().includes('processing')) return 1;
    if (status.toLowerCase().includes('transcribing')) return 2;
    if (status.toLowerCase().includes('analyzing')) return 3;
    if (status.toLowerCase().includes('frames') || status.toLowerCase().includes('extracting')) return 4;
    if (status.toLowerCase().includes('generating') || status.toLowerCase().includes('pdf')) return 5;
    return 1; // Default to step 1
  };

  const currentStep = getCurrentStep();
  const totalSteps = 5;

  return (
    <div className="text-center">
      <div className="mb-4">
        <span className="inline-block bg-blue-100 text-blue-800 text-sm font-medium px-3 py-1 rounded-full">
          Step {currentStep}/{totalSteps}
        </span>
      </div>
      <h3 className="text-xl font-semibold text-blue-600">Processing Video</h3>
      <p className="text-gray-600 my-4">{details}</p>
      <div className="flex justify-center my-6">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-blue-600"></div>
      </div>
      <button onClick={onReset} className="bg-gray-200 text-gray-700 font-semibold py-2 px-4 rounded-lg hover:bg-gray-300">Start Over</button>
    </div>
  );
};


