    if (code) {
        handleGoogleOAuthCallback(code);
        window.history.replaceState({}, document.title, window.location.pathname);
    }
  }, [authToken]);

  const checkAuthStatus = async () => {
    if (authToken) {
      try {
        const response = await fetch(`${API_BASE}/api/v1/auth/me`, {
          headers: { 'Authorization': `Bearer ${authToken}` }
        });

        if (response.ok) {
          const user = await response.json();
          setCurrentUser(user);
        } else {
          handleLogout();
        }
      } catch (error) {
        console.error('Auth check error:', error);
        handleLogout();
      }
    }
  };

  const handleAuthAction = async (url, body) => {
    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body),
      });
      const data = await response.json();
      if (response.ok) {
        localStorage.setItem('authToken', data.access_token);
        setAuthToken(data.access_token);
      } else {
        alert(data.detail || 'Authentication failed');
      }
    } catch (error) {
      console.error('Auth action error:', error);
      alert('An error occurred. Please try again.');
    }
  };

  const handleLogin = (e) => {
    e.preventDefault();
    const email = e.target.email.value;
    const password = e.target.password.value;
    handleAuthAction(`${API_BASE}/api/v1/auth/login`, { email, password });
  };

  const handleRegister = (e) => {
    e.preventDefault();
    const name = e.target.name.value;
    const email = e.target.email.value;
    const password = e.target.password.value;
    handleAuthAction(`${API_BASE}/api/v1/auth/register`, { full_name: name, email, password });
  };

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    setAuthToken(null);
    setCurrentUser(null);
  };

  const initiateGoogleSignIn = async () => {
      try {
          const response = await fetch(`${API_BASE}/api/v1/auth/google/url`);
          if (response.ok) {
              const data = await response.json();
              window.location.href = data.url;
          } else {
              console.error("Could not get Google auth URL");
          }
      } catch (error) {
          console.error("Error initiating Google sign in:", error);
      }
  };

  const handleGoogleOAuthCallback = async (code) => {
      try {
          const response = await fetch(`${API_BASE}/api/v1/auth/google/callback`, {
              method: 'POST',
              headers: { 'Content-Type': 'application/json' },
              body: JSON.stringify({ code }),
          });
          const data = await response.json();
          if (response.ok) {
              localStorage.setItem('authToken', data.access_token);
              setAuthToken(data.access_token);
          } else {
              alert(data.detail || 'Google sign-in failed');
          }
      } catch (error) {
          console.error('Google OAuth callback error:', error);
          alert('An error occurred during Google sign-in.');
      }
  };

  const searchYouTube = async () => {
    if (!searchQuery) return;
    setIsSearching(true);
    setSearchResults([]);
    setSelectedVideoId(null);
    try {
      const response = await fetch(`${API_BASE}/api/v1/search?query=${encodeURIComponent(searchQuery)}`);
      const data = await response.json();
      setSearchResults(data.videos || []);
    } catch (error) {
      console.error('Search error:', error);
    } finally {
      setIsSearching(false);
    }
  };

  const processVideo = async () => {
    if (!videoUrl) return;
    setJob({ status: 'starting', details: 'Initializing job...', job_id: 'temp' });
    try {
      const response = await fetch(`${API_BASE}/api/v1/generate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${authToken}`
        },
        body: JSON.stringify({ 
          youtube_url: videoUrl,
          language: language
        })
      });

      const data = await response.json();
      if (response.status === 202) {
        setJob({ ...data, status: data.status || 'processing', details: data.details || 'Job submitted.' });
      } else {
        setJob({ status: 'failed', details: data.detail || 'Failed to start job.' });
      }
    } catch (error) {
      console.error('Processing error:', error);
      setJob({ status: 'failed', details: 'An unexpected error occurred.' });
    }
  };

  const resetAll = () => {
    setVideoUrl('');
    setSearchQuery('recipe');
    setSearchResults([]);
    setSelectedVideoId(null);
    setJob(null);
  };

  if (job) {
    return (
      <div className="font-poppins bg-gray-50 min-h-screen flex items-center justify-center">
        <div className="w-full max-w-2xl bg-white p-8 sm:p-12 rounded-2xl shadow-lg">
          <JobStatus job={job} onReset={resetAll} />
        </div>
      </div>
    );
  }

  return (
    <div className="font-poppins bg-gradient-to-br from-green-50 to-cyan-50 min-h-screen">
      <Header currentUser={currentUser} handleLogout={handleLogout} initiateGoogleSignIn={initiateGoogleSignIn} />

      <main className="max-w-4xl mx-auto p-8 mt-10 bg-white/90 backdrop-blur-xl rounded-2xl shadow-2xl border border-gray-200/80">
        {!currentUser ? (
           <div className="max-w-md mx-auto text-center">
             <h1 className="text-4xl font-bold tracking-tight text-gray-800 sm:text-5xl">Welcome to Food2Guide</h1>
             <p className="mt-6 text-lg text-gray-600">Please sign in to continue. Create an account or use Google to get started instantly.</p>
             <AuthForm handleLogin={handleLogin} handleRegister={handleRegister} setAuthTab={setAuthTab} authTab={authTab} />
           </div>
        ) : (
          <>
            <div className="text-center mb-12">
              <h1 className="text-4xl font-bold tracking-tight text-gray-800 sm:text-5xl">
                Turn any food video into a <span className="text-green-600">beautiful guide</span>
              </h1>
              <p className="mt-4 text-lg text-gray-600 max-w-2xl mx-auto">
                Paste a link from YouTube, TikTok, or Instagram, and we'll transform it into an easy-to-follow PDF recipe, complete with step-by-step images.
              </p>
            </div>

            <div className="bg-white p-4 sm:p-6 rounded-2xl shadow-lg border border-gray-100">
              <div className="flex border-b border-gray-200">
                <button 
                  onClick={() => setActiveTab('paste')}
                  className={`flex-1 py-3 text-sm font-semibold transition-colors focus:outline-none ${activeTab === 'paste' ? 'text-green-600 border-b-2 border-green-600' : 'text-gray-500 hover:text-gray-700'}`}
                >
                  Paste Link
                </button>
                <button 
                  onClick={() => setActiveTab('search')}
                  className={`flex-1 py-3 text-sm font-semibold transition-colors focus:outline-none ${activeTab === 'search' ? 'text-green-600 border-b-2 border-green-600' : 'text-gray-500 hover:text-gray-700'}`}
                >
                  Search Video
                </button>
              </div>

              {activeTab === 'paste' ? (
                <div className="pt-6">
                  <div className="relative">
                    <LinkIcon className="h-5 w-5 text-gray-400 absolute left-4 top-1/2 -translate-y-1/2" />
                    <input 
                      type="text"
                      value={videoUrl}
                      onChange={(e) => setVideoUrl(e.target.value)}
                      placeholder="Paste your video link here (YouTube, TikTok, Instagram)" 
                      className="w-full pl-12 pr-4 py-3.5 border border-gray-300 rounded-xl shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 transition" 
                    />
                  </div>
                </div>
              ) : (
                <div className="pt-6">
                  <div className="relative">
                    <MagnifyingGlassIcon className="h-5 w-5 text-gray-400 absolute left-4 top-1/2 -translate-y-1/2" />
                    <input 
                      type="text"
                      value={searchQuery}
                      onChange={(e) => setSearchQuery(e.target.value)}
                      onKeyDown={(e) => e.key === 'Enter' && searchYouTube()}
                      placeholder="Search for a recipe video on YouTube..." 
                      className="w-full pl-12 pr-4 py-3.5 border border-gray-300 rounded-xl shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 transition" 
                    />
                  </div>
                  <button 
                    onClick={searchYouTube}
                    disabled={isSearching}
                    className="mt-4 w-full bg-gray-700 hover:bg-gray-800 text-white font-bold py-3 px-4 rounded-xl shadow-md hover:shadow-lg transition-all disabled:bg-gray-400"
                  >
                    {isSearching ? 'Searching...' : 'Search'}
                  </button>
                </div>
              )}
            </div>

            {searchResults.length > 0 && (
              <div className="mt-8">
                <h3 className="text-lg font-semibold text-gray-700 mb-4">Search Results</h3>
                <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                  {searchResults.map(video => (
                    <div 
                      key={video.video_id} 
                      onClick={() => {
                        setSelectedVideoId(video.video_id);
                        setVideoUrl(`https://www.youtube.com/watch?v=${video.video_id}`);
                      }}
                      className={`p-3 rounded-xl cursor-pointer transition-all ${selectedVideoId === video.video_id ? 'ring-4 ring-green-500 shadow-xl' : 'hover:shadow-lg hover:ring-2 hover:ring-green-300'}`}
                    >
                      <img src={video.thumbnail} alt={video.title} className="rounded-lg w-full" />
                      <p className="text-sm font-semibold mt-2 text-gray-800">{video.title}</p>
                      <p className="text-xs text-gray-500">{video.channel}</p>
                    </div>
                  ))}
                </div>
              </div>
            )}

            <div className="mt-8 flex flex-col sm:flex-row items-center justify-between gap-6 p-6 bg-gray-50 rounded-2xl border">
                <div className="relative">
                    <select 
                        value={language} 
                        onChange={(e) => setLanguage(e.target.value)}
                        className="appearance-none w-full sm:w-auto bg-white border border-gray-300 text-gray-700 py-3 pl-4 pr-10 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500"
                    >
                        {languages.map(lang => (
                            <option key={lang.code} value={lang.code}>{lang.flag} {lang.name}</option>
                        ))}
                    </select>
                    <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-700">
                        <svg className="fill-current h-4 w-4" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20"><path d="M9.293 12.95l.707.707L15.657 8l-1.414-1.414L10 10.828 5.757 6.586 4.343 8z"/></svg>
                    </div>
                </div>
                <button 
                    onClick={processVideo} 
                    disabled={!videoUrl}
                    className="w-full sm:w-auto flex items-center justify-center gap-3 bg-green-600 hover:bg-green-700 text-white font-bold py-4 px-8 rounded-xl shadow-lg hover:shadow-xl transition-all disabled:bg-green-300 disabled:cursor-not-allowed transform hover:scale-105"
                >
                    <FireIcon className="h-6 w-6" />
                    Generate Guide
                </button>
            </div>
          </>
        )}
      </main>
    </div>
  );
}
