import React, { useState, useEffect, useRef } from 'react';
import { 
  LinkIcon, 
  MagnifyingGlassIcon, 
  UserIcon,
  ArrowPathIcon,
  RocketLaunchIcon,
  CheckCircleIcon,
  ExclamationTriangleIcon,
  ArrowDownTrayIcon,
  ClockIcon,
  XMarkIcon,
  StarIcon,
  SparklesIcon
} from '@heroicons/react/24/outline';

const API_BASE = 'http://localhost:8000';

export default function Food2Guide() {
  // State management
  const [activeTab, setActiveTab] = useState('paste');
  const [videoUrl, setVideoUrl] = useState('');
  const [searchQuery, setSearchQuery] = useState('recipe');
  const [language, setLanguage] = useState('en');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedVideoId, setSelectedVideoId] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [currentJobId, setCurrentJobId] = useState(null);
  const [progress, setProgress] = useState(0);
  const [statusMessage, setStatusMessage] = useState('');
  const [downloadUrl, setDownloadUrl] = useState('');
  const [showAuthSection, setShowAuthSection] = useState(false);
  const [authTab, setAuthTab] = useState('login');
  const [authToken, setAuthToken] = useState(localStorage.getItem('authToken'));
  const [currentUser, setCurrentUser] = useState(null);
  const [showGuestModal, setShowGuestModal] = useState(false);
  const [isSearching, setIsSearching] = useState(false);

  const statusInterval = useRef(null);
  const progressInterval = useRef(null);
  const lastStatus = useRef(null);
  const globalProgress = useRef(0);
  const targetProgress = useRef(0);
  const currentStep = useRef(0);

  const stepLabels = [
    'Downloading video from YouTube',
    'Transcribing audio content', 
    'Analyzing content with AI',
    'Extracting key frames',
    'Generating PDF instructions'
  ];

  const languages = [
    { code: 'en', name: 'English', flag: 'ðŸ‡ºðŸ‡¸' },
    { code: 'sv', name: 'Svenska', flag: 'ðŸ‡¸ðŸ‡ª' },
    { code: 'no', name: 'Norsk', flag: 'ðŸ‡³ðŸ‡´' },
    { code: 'da', name: 'Dansk', flag: 'ðŸ‡©ðŸ‡°' },
    { code: 'de', name: 'Deutsch', flag: 'ðŸ‡©ðŸ‡ª' },
    { code: 'fr', name: 'FranÃ§ais', flag: 'ðŸ‡«ðŸ‡·' },
    { code: 'es', name: 'EspaÃ±ol', flag: 'ðŸ‡ªðŸ‡¸' },
    { code: 'it', name: 'Italiano', flag: 'ðŸ‡®ðŸ‡¹' },
    { code: 'pt', name: 'PortuguÃªs', flag: 'ðŸ‡µðŸ‡¹' },
    { code: 'nl', name: 'Nederlands', flag: 'ðŸ‡³ðŸ‡±' },
    { code: 'pl', name: 'Polski', flag: 'ðŸ‡µðŸ‡±' },
    { code: 'ru', name: 'Ð ÑƒÑÑÐºÐ¸Ð¹', flag: 'ðŸ‡·ðŸ‡º' },
    { code: 'zh', name: 'ä¸­æ–‡', flag: 'ðŸ‡¨ðŸ‡³' },
    { code: 'ja', name: 'æ—¥æœ¬èªž', flag: 'ðŸ‡¯ðŸ‡µ' },
    { code: 'ko', name: 'í•œêµ­ì–´', flag: 'ðŸ‡°ðŸ‡·' }
  ];

  // Effects
  useEffect(() => {
    checkAuthStatus();

    // Check for OAuth callback
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');
    const error = urlParams.get('error');

    if (code) {
      handleGoogleOAuthCallback(code);
    } else if (error) {
      alert(`OAuth Error: ${urlParams.get('error_description') || 'Authentication failed'}`);
      window.history.replaceState({}, document.title, window.location.pathname);
    }
  }, []);

  useEffect(() => {
    if (!authToken && !localStorage.getItem('guestModalSeen')) {
      setShowGuestModal(true);
    }
  }, [authToken]);

  // Auth functions
  const checkAuthStatus = async () => {
    if (authToken) {
      try {
        const response = await fetch(`${API_BASE}/api/v1/auth/me`, {
          headers: { 'Authorization': `Bearer ${authToken}` }
        });

        if (response.ok) {
          const user = await response.json();
          setCurrentUser(user);
        } else {
          localStorage.removeItem('authToken');
          setAuthToken(null);
        }
      } catch (error) {
        console.error('Auth check error:', error);
      }
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const email = formData.get('email');
    const password = formData.get('password');

    try {
      const response = await fetch(`${API_BASE}/api/v1/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });

      if (response.ok) {
        const data = await response.json();
        setAuthToken(data.access_token);
        setCurrentUser(data.user);
        localStorage.setItem('authToken', data.access_token);
        setShowAuthSection(false);
      } else {
        const error = await response.json();
        alert(error.detail || 'Login failed');
      }
    } catch (error) {
      alert('Login failed. Please try again.');
    }
  };

  const handleRegister = async (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const full_name = formData.get('name');
    const email = formData.get('email');
    const password = formData.get('password');

    try {
      const response = await fetch(`${API_BASE}/api/v1/auth/register`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ full_name, email, password })
      });

      if (response.ok) {
        const data = await response.json();
        setAuthToken(data.access_token);
        setCurrentUser(data.user);
        localStorage.setItem('authToken', data.access_token);
        setShowAuthSection(false);
      } else {
        const error = await response.json();
        alert(error.detail || 'Registration failed');
      }
    } catch (error) {
      alert('Registration failed. Please try again.');
    }
  };

  const handleLogout = () => {
    setAuthToken(null);
    setCurrentUser(null);
    localStorage.removeItem('authToken');
  };

  const initiateGoogleSignIn = async () => {
    try {
      const configResponse = await fetch(`${API_BASE}/api/v1/auth/google/config`);
      if (!configResponse.ok) {
        throw new Error('Failed to get Google OAuth config');
      }

      const config = await configResponse.json();

      if (!config.enabled || !config.client_id) {
        alert('Google Sign-In is not configured. Please use email/password authentication.');
        return;
      }

      const params = new URLSearchParams({
        client_id: config.client_id,
        redirect_uri: window.location.origin,
        response_type: 'code',
        scope: 'email profile',
        access_type: 'offline',
        prompt: 'select_account'
      });

      const googleAuthUrl = `https://accounts.google.com/o/oauth2/v2/auth?${params.toString()}`;
      window.location.href = googleAuthUrl;

    } catch (error) {
      console.error('Google Sign-In initialization error:', error);
      alert('Google Sign-In is not available. Please use email/password authentication.');
    }
  };

  const handleGoogleOAuthCallback = async (code) => {
    try {
      const authResponse = await fetch(`${API_BASE}/api/v1/auth/google/callback`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ code })
      });

      if (authResponse.ok) {
        const data = await authResponse.json();
        setAuthToken(data.access_token);
        setCurrentUser(data.user);
        localStorage.setItem('authToken', data.access_token);
        window.history.replaceState({}, document.title, window.location.pathname);
        setShowAuthSection(false);
      } else {
        const error = await authResponse.json();
        alert(error.detail || 'Google Sign-In failed');
        window.history.replaceState({}, document.title, window.location.pathname);
      }
    } catch (error) {
      console.error('Google OAuth callback error:', error);
      alert('Google Sign-In failed. Please try again.');
      window.history.replaceState({}, document.title, window.location.pathname);
    }
  };

  // Search functions
  const searchYouTube = async () => {
    if (!searchQuery.trim()) return;

    setIsSearching(true);
    try {
      const response = await fetch(`${API_BASE}/api/v1/search`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ query: searchQuery, language })
      });

      if (response.ok) {
        const data = await response.json();
        setSearchResults(data.videos || []);
      } else {
        throw new Error(`Search failed: ${response.status}`);
      }
    } catch (error) {
      console.error('Search error:', error);
      setSearchResults([]);
    } finally {
      setIsSearching(false);
    }
  };

  const selectVideo = (video) => {
    setSelectedVideoId(video.video_id);
    setSearchResults(prev => prev.map(v => ({
      ...v,
      selected: v.video_id === video.video_id
    })));
  };

  // Progress functions
  const updateStepsBasedOnStatus = (status) => {
    const statusUpper = status.toUpperCase();

    if (statusUpper === lastStatus.current) return;
    lastStatus.current = statusUpper;

    switch(statusUpper) {
      case 'PROCESSING':
        currentStep.current = 0;
        targetProgress.current = 20;
        break;
      case 'TRANSCRIBING':
        currentStep.current = 1;
        targetProgress.current = 50;
        break;
      case 'ANALYZING':
        currentStep.current = 2;
        targetProgress.current = 70;
        break;
      case 'EXTRACTING_FRAMES':
        currentStep.current = 3;
        targetProgress.current = 85;
        break;
      case 'GENERATING_PDF':
        currentStep.current = 4;
        targetProgress.current = 100;
        break;
      case 'COMPLETED':
        stopContinuousProgress();
        setProgress(100);
        setStatusMessage('COMPLETED: PDF generated successfully!');
        return;
      case 'FAILED':
        stopContinuousProgress();
        setProgress(0);
        setStatusMessage('FAILED: Processing failed');
        return;
    }

    startContinuousProgress();
  };

  const startContinuousProgress = () => {
    if (progressInterval.current) return;

    progressInterval.current = setInterval(() => {
      if (globalProgress.current < targetProgress.current) {
        const diff = targetProgress.current - globalProgress.current;
        const increment = Math.max(0.1, diff * 0.02);
        globalProgress.current = Math.min(globalProgress.current + increment, targetProgress.current);
      } else if (globalProgress.current < 100 && targetProgress.current < 100) {
        globalProgress.current = Math.min(globalProgress.current + 0.05, targetProgress.current + 5);
      }

      const stepNum = currentStep.current + 1;
      const message = `STEP ${stepNum}/5: ${stepLabels[currentStep.current]}`;
      setProgress(Math.round(globalProgress.current));
      setStatusMessage(message);

      if (globalProgress.current >= 100) {
        stopContinuousProgress();
      }
    }, 50);
  };

  const stopContinuousProgress = () => {
    if (progressInterval.current) {
      clearInterval(progressInterval.current);
      progressInterval.current = null;
    }
  };

  const resetSteps = () => {
    stopContinuousProgress();
    globalProgress.current = 0;
    targetProgress.current = 0;
    currentStep.current = 0;
    setProgress(0);
    setStatusMessage('');
    lastStatus.current = null;
  };

  // Video processing
  const processVideo = async () => {
    let url = '';

    if (activeTab === 'paste') {
      url = videoUrl.trim();
      if (!url) {
        alert('Please paste a video URL');
        return;
      }

      if (!isValidUrl(url)) {
        alert('Please enter a valid YouTube or TikTok URL.');
        return;
      }
    } else if (activeTab === 'search') {
      if (!selectedVideoId) {
        alert('Please select a video from the search results');
        return;
      }
      url = `https://www.youtube.com/watch?v=${selectedVideoId}`;
    }

    setIsProcessing(true);
    resetSteps();

    try {
      const headers = { 'Content-Type': 'application/json' };
      if (authToken) {
        headers['Authorization'] = `Bearer ${authToken}`;
      }

      const response = await fetch(`${API_BASE}/api/v1/generate`, {
        method: 'POST',
        headers,
        body: JSON.stringify({ youtube_url: url, language })
      });

      if (!response.ok) {
        if (response.status === 401) {
          alert('Your session has expired. Please sign in again.');
          setAuthToken(null);
          localStorage.removeItem('authToken');
          setShowAuthSection(true);
          return;
        }
        if (response.status === 429) {
          const error = await response.json();
          alert(error.detail || 'Too many requests. Create a free account for unlimited usage.');
          return;
        }
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const data = await response.json();
      setCurrentJobId(data.job_id);

      globalProgress.current = 0;
      targetProgress.current = 20;
      currentStep.current = 0;
      lastStatus.current = null;

      updateStepsBasedOnStatus('PROCESSING');

      // Start polling for status
      statusInterval.current = setInterval(async () => {
        try {
          const statusHeaders = {};
          if (authToken) {
            statusHeaders['Authorization'] = `Bearer ${authToken}`;
          }

          const statusResponse = await fetch(`${API_BASE}/api/v1/status/${data.job_id}`, {
            headers: statusHeaders
          });

          if (!statusResponse.ok) {
            throw new Error(`Status check failed: ${statusResponse.status}`);
          }

          const statusData = await statusResponse.json();
          updateStepsBasedOnStatus(statusData.status);

          if (statusData.status.toUpperCase() === 'COMPLETED') {
            clearInterval(statusInterval.current);
            statusInterval.current = null;
            setDownloadUrl(`${API_BASE}/api/v1/result/${data.job_id}`);
            setIsProcessing(false);
          } else if (statusData.status.toUpperCase() === 'FAILED') {
            clearInterval(statusInterval.current);
            statusInterval.current = null;
            setIsProcessing(false);
            setStatusMessage(`Error: ${statusData.error || 'Unknown error'}`);
          }
        } catch (error) {
          console.error('Error checking status:', error);
          clearInterval(statusInterval.current);
          statusInterval.current = null;
          setIsProcessing(false);
          setStatusMessage('Error checking status');
        }
      }, 1000);

    } catch (error) {
      console.error('Error:', error);
      setIsProcessing(false);
      setStatusMessage('Error processing video');
    }
  };

  const downloadPDF = async () => {
    if (!downloadUrl) return;

    try {
      const headers = {};
      if (authToken) {
        headers['Authorization'] = `Bearer ${authToken}`;
      }

      const response = await fetch(downloadUrl, { headers });

      if (!response.ok) {
        throw new Error('Download failed');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;
      link.download = 'Food2Guide_Recipe.pdf';
      link.click();
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error('Download failed:', error);
      alert('Download failed');
    }
  };

  const isValidUrl = (url) => {
    const youtubePatterns = [
      /(?:https?:\/\/)?(?:www\.)?youtube\.com\/watch\?v=[\w-]+/,
      /(?:https?:\/\/)?(?:www\.)?youtube\.com\/shorts\/[\w-]+/,
      /(?:https?:\/\/)?youtu\.be\/[\w-]+/
    ];

    const tiktokPatterns = [
      /(?:https?:\/\/)?(?:www\.)?tiktok\.com\/@[^\/]+\/video\/\d+/,
      /(?:https?:\/\/)?(?:vm|vt)\.tiktok\.com\/[A-Za-z0-9]+/
    ];

    return [...youtubePatterns, ...tiktokPatterns].some(pattern => pattern.test(url.trim()));
  };

