import React, { useState, useEffect } from 'react';
import { 
  LinkIcon, 
  MagnifyingGlassIcon, 
  ArrowDownTrayIcon,
  SparklesIcon,
} from '@heroicons/react/24/outline';

const API_BASE_URL = 'http://localhost:8000/api/v1';

const Spinner = () => (
  <svg className="animate-spin h-5 w-5 text-green-600" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
  </svg>
);

const RecipeStreamViewer = ({ status, error, data, onReset }) => {
  if (error) {
    return (
      <div className="bg-white p-8 rounded-2xl shadow-lg animate-fade-in text-center">
        <h3 className="text-xl font-semibold text-red-600">Generation Failed</h3>
        <p className="text-gray-600 my-4">{error}</p>
        <button onClick={onReset} className="bg-gray-700 text-white font-bold py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors">
          Start Over
        </button>
      </div>
    );
  }

  return (
    <div className="bg-white p-8 rounded-2xl shadow-lg animate-fade-in">
      <div className="text-left mb-8">
        <h2 className="text-3xl font-bold text-gray-800 mb-4">{data?.title || "Generating Recipe..."}</h2>
        <p className="text-gray-600 mb-6 italic">{status}</p>

        {data ? (
          <div className="grid md:grid-cols-3 gap-8">
            <div className="md:col-span-1">
              <h4 className="text-xl font-bold text-gray-800 mb-4 border-b-2 border-green-400 pb-2">Ingredients</h4>
              {data.ingredients && data.ingredients.length > 0 ? (
                <ul className="list-disc list-inside text-gray-700 space-y-2">
                  {data.ingredients.map((ing, i) => (
                    <li key={i} className="animate-fade-in">
                      <span className="font-semibold">{ing.quantity}</span> {ing.name}
                    </li>
                  ))}
                </ul>
              ) : <Spinner />}
            </div>
            <div className="md:col-span-2">
              <h4 className="text-xl font-bold text-gray-800 mb-4 border-b-2 border-green-400 pb-2">Instructions</h4>
              {data.instructions && data.instructions.length > 0 ? (
                <ol className="list-decimal list-inside text-gray-700 space-y-3">
                  {data.instructions.map((step, i) => (
                    <li key={i} className="animate-fade-in">{step}</li>
                  ))}
                </ol>
              ) : <Spinner />}
            </div>
          </div>
        ) : (
          <div className="text-center py-8">
            <Spinner />
          </div>
        )}
      </div>

      {status === 'Recipe generation complete.' && (
         <div className="mt-8 flex gap-4 justify-center">
          <button onClick={() => alert("Not implemented yet")} className="inline-flex items-center gap-2 bg-green-600 text-white font-bold py-3 px-6 rounded-lg hover:bg-green-700 transition-colors">
            <ArrowDownTrayIcon className="h-5 w-5" /> Download PDF
          </button>
          <button onClick={onReset} className="bg-gray-200 text-gray-800 font-bold py-3 px-6 rounded-lg hover:bg-gray-300 transition-colors">Create Another</button>
        </div>
      )}
    </div>
  );
};


export default function Food2Guide() {
  const [videoUrl, setVideoUrl] = useState('');
  const [language, setLanguage] = useState('en');
  const [isStreaming, setIsStreaming] = useState(false);
  const [recipeData, setRecipeData] = useState(null);
  const [streamError, setStreamError] = useState(null);
  const [streamStatus, setStreamStatus] = useState('');
  const [authToken] = useState(localStorage.getItem('authToken'));

  const languages = [
    { code: 'en', name: 'English' },
    { code: 'sv', name: 'Svenska' },
    { code: 'no', name: 'Norsk' },
    { code: 'da', name: 'Dansk' },
    { code: 'de', name: 'Deutsch' },
    { code: 'fr', name: 'Français' },
    { code: 'es', name: 'Español' },
  ];

  const processVideo = async () => {
    if (!videoUrl) {
      alert('Please provide a video URL.');
      return;
    }

    setIsStreaming(true);
    setRecipeData(null);
    setStreamError(null);
    setStreamStatus('Initializing stream...');

    try {
      const headers = { 'Content-Type': 'application/json' };
      if (authToken) {
        headers['Authorization'] = `Bearer ${authToken}`;
      }

      const response = await fetch(`${API_BASE_URL}/generate-stream`, {
        method: 'POST',
        headers,
        body: JSON.stringify({
          video_url: videoUrl,
          language: language
        })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.detail || 'Failed to start stream');
      }

      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let buffer = "";

      while (true) {
        const { done, value } = await reader.read();
        if (done) {
          setStreamStatus('Recipe generation complete.');
          break;
        }

        buffer += decoder.decode(value, { stream: true });
        const lines = buffer.split('\\n');
        buffer = lines.pop(); 

        for (const line of lines) {
          if (line.startsWith('data: ')) {
            const jsonStr = line.substring(6);
            if (!jsonStr) continue;

            try {
              const eventData = JSON.parse(jsonStr);

              if (eventData.status) {
                if (eventData.status === 'error') {
                  setStreamError(eventData.message);
                  setStreamStatus('An error occurred.');
                  return; 
                } else {
                  setStreamStatus(eventData.message);
                }
              } else if (eventData.content) { 
                setRecipeData(prevData => ({...prevData, ...eventData.content}));
              }
            } catch (e) {
              // Ignore parsing errors for incomplete JSON
            }
          }
        }
      }
    } catch (e) {
      setStreamError(e.message);
      setStreamStatus('Failed to connect to the server.');
    }
  };

  const handleReset = () => {
    setIsStreaming(false);
    setRecipeData(null);
    setStreamError(null);
    setStreamStatus('');
    setVideoUrl('');
  };

  if (isStreaming) {
    return (
        <div className="font-poppins bg-gray-50 min-h-screen p-8">
            <div className="max-w-2xl mx-auto">
                <RecipeStreamViewer
                    status={streamStatus}
                    error={streamError}
                    data={recipeData}
                    onReset={handleReset}
                />
            </div>
      </div>
    );
  }

  return (
    <div className="font-poppins bg-gray-50 min-h-screen flex items-center justify-center p-4">
        <main className="max-w-xl w-full">
            <div className="relative bg-white shadow-2xl rounded-2xl p-10">
                <SparklesIcon className="absolute top-0 right-0 h-32 w-32 text-green-300/30 -translate-y-1/3 translate-x-1/4" />
                <div className="text-center mb-8">
                    <h1 className="text-4xl font-bold tracking-tight text-gray-800">
                        Food2Guide
                    </h1>
                    <p className="text-gray-500 mt-2">Turn cooking videos into step-by-step recipes.</p>
                </div>

                <div className="space-y-4">
                    <div>
                        <label htmlFor="video-url" className="sr-only">Video URL</label>
                        <div className="relative">
                            <LinkIcon className="absolute left-4 top-1/2 -translate-y-1/2 h-5 w-5 text-gray-400"/>
                            <input
                                id="video-url"
                                type="text"
                                value={videoUrl}
                                onChange={(e) => setVideoUrl(e.target.value)}
                                placeholder="Paste a YouTube or TikTok recipe video…"
                                className="w-full pl-12 pr-4 py-3 border-2 border-gray-200 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 text-md"
                            />
                        </div>
                    </div>
                    <div>
                        <label htmlFor="language-select" className="sr-only">Language</label>
                        <select
                            id="language-select"
                            value={language}
                            onChange={(e) => setLanguage(e.target.value)}
                            className="w-full px-4 py-3 border-2 border-gray-200 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 text-md text-gray-700 bg-white"
                        >
                            {languages.map(lang => <option key={lang.code} value={lang.code}>{lang.name}</option>)}
                        </select>
                    </div>
                </div>

                <div className="pt-6 mt-6 border-t border-gray-200">
                    <button onClick={processVideo} className="w-full flex items-center justify-center gap-3 bg-green-600 hover:bg-green-700 text-white font-bold rounded-xl shadow-md px-6 py-4 text-lg transition-transform hover:scale-[1.02]">
                        <MagnifyingGlassIcon className="h-6 w-6" />
                        <span>Generate Recipe</span>
                    </button>
                </div>
            </div>
        </main>
    </div>
  );
}
