const ProgressIndicator = ({ status, details }) => {
  const states = [
    { key: 'downloading', text: 'Downloading' },
    { key: 'transcribing', text: 'Transcribing' },
    { key: 'analyzing', text: 'Analyzing' },
    { key: 'extracting_frames', text: 'Extracting Frames' },
    { key: 'generating_pdf', text: 'Generating PDF' },
  ];

  const currentStatusIndex = states.findIndex(s => status.toLowerCase().includes(s.key));

  return (
    <div className="w-full max-w-lg text-center">
      <div className="relative pt-1">
        <div className="overflow-hidden h-2 mb-4 text-xs flex rounded bg-green-200">
          <div style={{ width: `${((currentStatusIndex + 1) / states.length) * 100}%` }} className="shadow-none flex flex-col text-center whitespace-nowrap text-white justify-center bg-green-500 transition-all duration-500"></div>
        </div>
      </div>
      <div className="flex justify-between text-xs text-gray-500">
        {states.map((state, index) => (
          <div key={state.key} className={`flex-1 text-center ${index <= currentStatusIndex ? 'font-bold text-gray-800' : ''}`}>
            {state.text}
          </div>
        ))}
      </div>
      <p className="text-gray-600 mt-4">{details}</p>
    </div>
  );
};


const JobStatus = ({ job, onReset }) => {
  const [status, setStatus] = useState(job.status);
  const [details, setDetails] = useState(job.details);
  const [pdfUrl, setPdfUrl] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (status === 'completed' || status === 'failed') {
      if (job.pdf_url) {
        setPdfUrl(job.pdf_url);
      }
      return;
    }

    const interval = setInterval(async () => {
      try {
        const response = await fetch(`${API_BASE}/api/v1/status/${job.job_id}`);
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();
        setStatus(data.status);
        setDetails(data.details);
        if (data.status === 'completed') {
          setPdfUrl(data.pdf_url);
          clearInterval(interval);
        }
        if (data.status === 'failed') {
          setError(data.details);
          clearInterval(interval);
        }
      } catch (err) {
        setError('Failed to fetch status.');
        clearInterval(interval);
      }
    }, 2000);

    return () => clearInterval(interval);
  }, [job.job_id, status, job.pdf_url]);

  if (status === 'failed') {
    return (
      <div className="text-center">
        <h3 className="text-xl font-semibold text-red-600">Generation Failed</h3>
        <p className="text-gray-600 my-4">{error || details}</p>
        <button
          onClick={onReset}
          className="bg-gray-700 text-white font-bold py-2 px-4 rounded-lg hover:bg-gray-800 transition-colors"
        >
          Start Over
        </button>
      </div>
    );
  }

  if (status === 'completed' && pdfUrl) {
    return (
      <div className="text-center">
        <h3 className="text-xl font-semibold text-green-600">Recipe Ready!</h3>
        <p className="text-gray-600 my-4">Your recipe has been generated successfully.</p>
        <div className="flex gap-4 justify-center">
          <a
            href={`${API_BASE}${pdfUrl}`}
            target="_blank"
            rel="noopener noreferrer"
            className="inline-flex items-center gap-2 bg-green-600 text-white font-bold py-3 px-6 rounded-lg hover:bg-green-700 transition-colors"
          >
            <ArrowDownTrayIcon className="h-5 w-5" />
            Download PDF
          </a>
          <button
            onClick={onReset}
            className="bg-gray-200 text-gray-800 font-bold py-3 px-6 rounded-lg hover:bg-gray-300 transition-colors"
          >
            Create Another
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="w-full flex flex-col items-center">
       <ProgressIndicator status={status} details={details} />
       <button
        onClick={onReset}
        className="mt-8 bg-gray-200 text-gray-700 font-semibold py-2 px-4 rounded-lg hover:bg-gray-300"
      >
        Cancel
      </button>
    </div>
  );
};


