import React, { useState, useEffect, useRef } from 'react';
import { 
  LinkIcon, 
  MagnifyingGlassIcon,
  CheckCircleIcon,
  ArrowDownTrayIcon,
  ArrowRightOnRectangleIcon,
  UserCircleIcon
} from '@heroicons/react/24/outline';

const API_BASE = 'http://localhost:8000';

const Header = ({ currentUser, handleLogout }) => (
  <header className="bg-transparent">
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
      <div className="flex justify-between items-center py-6">
        <div className="flex items-center space-x-4">
          <img className="h-10 w-auto" src="/logo.png" alt="Food2Guide" />
        </div>
        {currentUser && (
          <div className="flex items-center space-x-6">
            <div className="flex items-center space-x-2">
                <UserCircleIcon className="h-6 w-6 text-gray-500" />
                <span className="text-base text-gray-700 font-medium">Welcome, {currentUser.full_name || currentUser.email}</span>
            </div>
            <button 
              onClick={handleLogout} 
              className="flex items-center space-x-2 text-sm text-gray-600 hover:text-red-600 transition-transform transform hover:scale-105"
            >
              <ArrowRightOnRectangleIcon className="h-5 w-5" />
              <span>Sign Out</span>
            </button>
          </div>
        )}
      </div>
    </div>
  </header>
);

const AuthForm = ({ handleLogin, handleRegister, setAuthTab, authTab }) => (
    <div className="mt-8">
        <div className="flex border-b">
            <button onClick={() => setAuthTab('login')} className={`px-4 py-2 text-lg font-medium ${authTab === 'login' ? 'border-b-2 border-green-600 text-green-600' : 'text-gray-500'}`}>Login</button>
            <button onClick={() => setAuthTab('register')} className={`px-4 py-2 text-lg font-medium ${authTab === 'register' ? 'border-b-2 border-green-600 text-green-600' : 'text-gray-500'}`}>Register</button>
        </div>
        <div className="pt-8">
            {authTab === 'login' ? (
                <form onSubmit={handleLogin} className="space-y-6">
                    <div>
                        <label htmlFor="login-email" className="block text-sm font-medium text-gray-700">Email</label>
                        <input type="email" name="email" id="login-email" required className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-green-500 focus:border-green-500 text-base" />
                    </div>
                    <div>
                        <label htmlFor="login-password" className="block text-sm font-medium text-gray-700">Password</label>
                        <input type="password" name="password" id="login-password" required className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-green-500 focus:border-green-500 text-base" />
                    </div>
                    <button type="submit" className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-3 px-4 rounded-md shadow-md hover:shadow-lg transition-transform transform hover:scale-[1.03]">Sign In</button>
                </form>
            ) : (
                 <form onSubmit={handleRegister} className="space-y-8">
                    <div>
                        <label htmlFor="register-name" className="block text-sm font-medium text-gray-700">Full Name</label>
                        <input type="text" name="name" id="register-name" required className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-green-500 focus:border-green-500 text-base" />
                    </div>
                    <div>
                        <label htmlFor="register-email" className="block text-sm font-medium text-gray-700">Email</label>
                        <input type="email" name="email" id="register-email" required className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-green-500 focus:border-green-500 text-base" />
                    </div>
                    <div>
                        <label htmlFor="register-password" className="block text-sm font-medium text-gray-700">Password</label>
                        <input type="password" name="password" id="register-password" required className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-green-500 focus:border-green-500 text-base" />
                    </div>
                    <button type="submit" className="w-full bg-green-600 hover:bg-green-700 text-white font-bold py-3 px-4 rounded-md shadow-md hover:shadow-lg transition-transform transform hover:scale-[1.03]">Create Account</button>
                </form>
            )}
        </div>
    </div>
);


export default function Food2Guide() {
  const [activeTab, setActiveTab] = useState('paste');
  const [videoUrl, setVideoUrl] = useState('');
  const [searchQuery, setSearchQuery] = useState('recipe');
  const [language, setLanguage] = useState('en');
  const [searchResults, setSearchResults] = useState([]);
  const [selectedVideoId, setSelectedVideoId] = useState(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [currentJobId, setCurrentJobId] = useState(null);
  const [progress, setProgress] = useState(0);
  const [statusMessage, setStatusMessage] = useState('');
  const [downloadUrl, setDownloadUrl] = useState('');
  const [authToken, setAuthToken] = useState(localStorage.getItem('authToken'));
  const [currentUser, setCurrentUser] = useState(null);
  const [authTab, setAuthTab] = useState('login');
  const [isSearching, setIsSearching] = useState(false);

  const statusInterval = useRef(null);

  const stepLabels = [
    'Downloading', 'Transcribing', 'Analyzing', 'Extracting Frames', 'Generating PDF'
  ];

  const languages = [
    { code: 'en', name: 'English', flag: 'ðŸ‡ºðŸ‡¸' },
    { code: 'sv', name: 'Svenska', flag: 'ðŸ‡¸ðŸ‡ª' },
    { code: 'no', name: 'Norsk', flag: 'ðŸ‡³ðŸ‡´' },
    { code: 'da', name: 'Dansk', flag: 'ðŸ‡©ðŸ‡°' },
    { code: 'de', name: 'Deutsch', flag: 'ðŸ‡©ðŸ‡ª' },
    { code: 'fr', name: 'FranÃ§ais', flag: 'ðŸ‡«ðŸ‡·' },
    { code: 'es', name: 'EspaÃ±ol', flag: 'ðŸ‡ªðŸ‡¸' },
  ];

  useEffect(() => {
    const token = localStorage.getItem('authToken');
    if (token) {
      setAuthToken(token);
    }
  }, []);

  useEffect(() => {
    if (authToken) {
      checkAuthStatus();
    } else {
      setCurrentUser(null);
    }

    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');
    if (code) {
        handleGoogleOAuthCallback(code);
        window.history.replaceState({}, document.title, window.location.pathname);
    }
  }, [authToken]);

  const checkAuthStatus = async () => {
    if (authToken) {
      try {
        const response = await fetch(`${API_BASE}/api/v1/auth/me`, {
          headers: { 'Authorization': `Bearer ${authToken}` }
        });

        if (response.ok) {
          const user = await response.json();
          setCurrentUser(user);
        } else {
          handleLogout();
        }
      } catch (error) {
        console.error('Auth check error:', error);
        handleLogout();
      }
    }
  };

  const handleAuthAction = async (url, body) => {
    try {
      const response = await fetch(url, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(body)
      });

      if (response.ok) {
        const data = await response.json();
        localStorage.setItem('authToken', data.access_token);
        setAuthToken(data.access_token);
      } else {
        const error = await response.json();
        alert(error.detail || 'Action failed');
      }
    } catch (error) {
      alert('An error occurred. Please try again.');
    }
  };

  const handleLogin = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const body = Object.fromEntries(formData.entries());
    handleAuthAction(`${API_BASE}/api/v1/auth/login`, body);
  };

  const handleRegister = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);
    const body = {
      full_name: formData.get('name'),
      email: formData.get('email'),
      password: formData.get('password')
    };
    handleAuthAction(`${API_BASE}/api/v1/auth/register`, body);
  };

  const handleLogout = () => {
    setAuthToken(null);
    setCurrentUser(null);
    localStorage.removeItem('authToken');
  };

  const initiateGoogleSignIn = async () => {
    try {
      const urlResponse = await fetch(`${API_BASE}/api/v1/auth/google/url`);
      if (!urlResponse.ok) throw new Error('Failed to get Google OAuth URL');
      const urlData = await urlResponse.json();
      if (!urlData.auth_url) {
          alert('Google Sign-In is not available.');
          return;
      }
      window.location.href = urlData.auth_url;
    } catch (error) {
      console.error('Google Sign-In error:', error);
      alert('Could not initiate Google Sign-In.');
    }
  };

  const handleGoogleOAuthCallback = async (code) => {
    try {
        const authResponse = await fetch(`${API_BASE}/api/v1/auth/google/callback`, {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ code })
        });
        if (authResponse.ok) {
            const data = await authResponse.json();
            localStorage.setItem('authToken', data.access_token);
            setAuthToken(data.access_token);
        } else {
            const error = await authResponse.json();
            alert(error.detail || 'Google Sign-In failed');
        }
    } catch (error) {
        console.error('Google OAuth callback error:', error);
        alert('Google Sign-In failed.');
    }
  };

  const searchYouTube = async () => {
    if (!searchQuery.trim()) return;
    setIsSearching(true);
    try {
      const response = await fetch(`${API_BASE}/api/v1/search`, {
        method: 'POST',
        headers: { 
            'Content-Type': 'application/json',
            'Authorization': `Bearer ${authToken}` 
        },
        body: JSON.stringify({ query: searchQuery, language })
      });
      if (response.ok) {
        const data = await response.json();
        setSearchResults(data.results);
      } else {
        alert('Search failed.');
      }
    } catch (error) {
      alert('An error occurred during search.');
    } finally {
        setIsSearching(false);
    }
  };

  const processVideo = async () => {
    if (!videoUrl && !selectedVideoId) {
      alert('Please provide a video URL or select a video.');
      return;
    }

    setIsProcessing(true);
    setDownloadUrl('');
    setProgress(0);
    setStatusMessage('Initializing...');

    try {
      const response = await fetch(`${API_BASE}/api/v1/generate`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${authToken}`
        },
        body: JSON.stringify({
          url: videoUrl,
          video_id: selectedVideoId,
          language: language,
        }),
      });

      if (response.status === 202) {
        const data = await response.json();
        setCurrentJobId(data.job_id);
      } else {
        const error = await response.json();
        setStatusMessage(`Error: ${error.detail}`);
        setIsProcessing(false);
      }
    } catch (error) {
      setStatusMessage('An unexpected error occurred.');
      setIsProcessing(false);
    }
  };

  useEffect(() => {
    if (isProcessing && currentJobId) {
      statusInterval.current = setInterval(async () => {
        try {
          const response = await fetch(`${API_BASE}/api/v1/status/${currentJobId}`);
          if (response.ok) {
            const data = await response.json();
            setStatusMessage(data.status_message);
            setProgress(data.progress);

            if (data.status === 'completed') {
              setDownloadUrl(`${API_BASE}/output/${data.filename}`);
              setIsProcessing(false);
              setCurrentJobId(null);
            } else if (data.status === 'failed') {
              setStatusMessage(`Error: ${data.error}`);
              setIsProcessing(false);
              setCurrentJobId(null);
            }
          }
        } catch (error) {
          setStatusMessage('Failed to get status.');
          setIsProcessing(false);
        }
      }, 2000);
    }
    return () => {
      if (statusInterval.current) {
        clearInterval(statusInterval.current);
      }
    };
  }, [isProcessing, currentJobId]);

  const resetAll = () => {
    setVideoUrl('');
    setSearchQuery('recipe');
    setSearchResults([]);
    setSelectedVideoId(null);
    setIsProcessing(false);
    setCurrentJobId(null);
    setProgress(0);
    setStatusMessage('');
    setDownloadUrl('');
    if(statusInterval.current) clearInterval(statusInterval.current);
  };

  if (!authToken || !currentUser) {
    return (
        <div className="font-poppins bg-gradient-to-br from-[#f9fafb] via-[#f2f7f5] to-[#e9f5f2] min-h-screen flex flex-col">
            <Header currentUser={null} />
            <div className="flex-grow flex items-center justify-center">
                <div className="max-w-md w-full bg-white p-8 rounded-2xl shadow-xl border border-gray-200">
                    <h2 className="text-3xl font-bold text-center text-gray-800">Welcome to Food2Guide</h2>
                    <p className="text-center text-gray-600 mt-4">Please sign in to continue</p>
                    <div className="mt-8">
                        <button 
                          onClick={initiateGoogleSignIn} 
                          className="w-full flex items-center justify-center py-3 px-4 border border-gray-300 rounded-lg shadow-md hover:shadow-lg bg-white text-base font-medium text-gray-700 hover:bg-gray-50 transition-transform transform hover:scale-[1.03]"
                        >
                            <img className="h-6 w-6 mr-3" src="https://upload.wikimedia.org/wikipedia/commons/c/c1/Google_%22G%22_logo.svg" alt="Google logo" />
                            Sign in with Google
                        </button>
                    </div>
                    <div className="mt-8 relative">
                        <div className="absolute inset-0 flex items-center">
                            <div className="w-full border-t border-gray-300" />
                        </div>
                        <div className="relative flex justify-center text-sm">
                            <span className="px-3 bg-white text-gray-500">Or continue with</span>
                        </div>
                    </div>
                    <AuthForm handleLogin={handleLogin} handleRegister={handleRegister} setAuthTab={setAuthTab} authTab={authTab} />
                </div>
            </div>
        </div>
    );
  }

  return (
    <div className="font-poppins bg-gradient-to-br from-[#f9fafb] via-[#f2f7f5] to-[#e9f5f2] min-h-screen">
      <Header currentUser={currentUser} handleLogout={handleLogout} />

      <main className="max-w-4xl mx-auto py-12 px-4 sm:px-6 lg:px-8">
        <div className="bg-white/70 backdrop-blur-sm rounded-2xl border border-gray-200 shadow-xl py-10 px-10">

          <h1 className="text-4xl font-bold text-gray-900 text-center">Transform any Video into a PDF Guide</h1>
          <p className="mt-4 text-center text-lg text-gray-600">Paste a video link or search YouTube to get started.</p>

          <div className="mt-10 space-y-8">
            <div>
              <div className="flex justify-center gap-2">
                <button
                  onClick={() => setActiveTab('paste')}
                  className={`flex items-center gap-2 rounded-full px-5 py-2 transition-all duration-150 ${
                    activeTab === 'paste'
                      ? 'bg-green-600 text-white font-semibold shadow-sm'
                      : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                  }`}
                >
                  <LinkIcon className={`h-6 w-6 ${activeTab === 'paste' ? 'text-white' : 'text-gray-600'}`} />
                  <span>Paste Link</span>
                </button>
                <button
                  onClick={() => setActiveTab('search')}
                  className={`flex items-center gap-2 rounded-full px-5 py-2 transition-all duration-150 ${
                    activeTab === 'search'
                      ? 'bg-green-600 text-white font-semibold shadow-sm'
                      : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
                  }`}
                >
                  <MagnifyingGlassIcon className={`h-6 w-6 ${activeTab === 'search' ? 'text-white' : 'text-gray-600'}`} />
                  <span>Search YouTube</span>
                </button>
              </div>

              <div className="mt-8">
                {activeTab === 'paste' && (
                  <div className="space-y-6">
                    <label htmlFor="video-url" className="text-xl font-bold text-gray-800">Video URL</label>
                    <p className="text-sm text-gray-500 -mt-4">Supports YouTube, TikTok, and more.</p>
                    <input
                      id="video-url"
                      type="text"
                      value={videoUrl}
                      onChange={(e) => { setVideoUrl(e.target.value); setSelectedVideoId(null); }}
                      placeholder="https://www.youtube.com/watch?v=..."
                      className="w-full px-4 py-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 text-base"
                    />
                  </div>
                )}
                {activeTab === 'search' && (
                  <div className="space-y-6">
                    <label htmlFor="search-query" className="text-xl font-bold text-gray-800">Search on YouTube</label>
                    <p className="text-sm text-gray-500 -mt-4">Find a recipe or how-to guide.</p>
                    <div className="flex gap-4">
                      <input
                        id="search-query"
                        type="text"
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        onKeyPress={(e) => e.key === 'Enter' && searchYouTube()}
                        placeholder="e.g., 'Gordon Ramsay scrambled eggs'"
                        className="flex-grow px-4 py-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 text-base"
                      />
                      <button onClick={searchYouTube} disabled={isSearching} className="bg-gray-800 text-white px-6 py-3 rounded-lg hover:bg-gray-900 shadow-md hover:shadow-lg transition-transform transform hover:scale-[1.03] disabled:bg-gray-400">
                        {isSearching ? 'Searching...' : 'Search'}
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </div>

            {searchResults.length > 0 && (
              <div>
                <h3 className="text-xl font-bold text-gray-800">Search Results</h3>
                <div className="mt-4 grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                  {searchResults.map((video) => (
                    <div 
                      key={video.id} 
                      onClick={() => { setSelectedVideoId(video.id); setVideoUrl(`https://www.youtube.com/watch?v=${video.id}`); }}
                      className={`cursor-pointer border-4 rounded-lg overflow-hidden transition-all duration-200 ${selectedVideoId === video.id ? 'border-green-500 shadow-2xl scale-105' : 'border-transparent hover:border-green-400'}`}
                    >
                      <img src={video.thumbnail} alt={video.title} className="w-full h-32 object-cover" />
                      <div className="p-3">
                        <p className="font-semibold text-sm text-gray-800 line-clamp-2">{video.title}</p>
                        <p className="text-xs text-gray-500 mt-1">{video.channel}</p>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            <div className="space-y-6">
                <label htmlFor="language-select" className="text-xl font-bold text-gray-800">Guide Language</label>
                <p className="text-sm text-gray-500 -mt-4">Select language for the generated PDF.</p>
                 <select 
                    id="language-select"
                    value={language}
                    onChange={(e) => setLanguage(e.target.value)}
                    className="w-full px-4 py-3 border border-gray-300 rounded-lg shadow-sm focus:outline-none focus:ring-2 focus:ring-green-500 text-base"
                >
                    {languages.map(lang => <option key={lang.code} value={lang.code}>{lang.flag} {lang.name}</option>)}
                </select>
            </div>

            <div className="pt-8 border-t border-gray-200 flex flex-col items-center space-y-6">
              {!isProcessing && !downloadUrl && (
                <button onClick={processVideo} className="w-full max-w-xs bg-green-600 hover:bg-green-700 text-white font-bold py-4 px-6 rounded-lg text-lg shadow-md hover:shadow-lg transition-transform transform hover:scale-[1.03]">
                  Create PDF Guide
                </button>
              )}

              {isProcessing && (
                <div className="w-full text-center">
                  <p className="text-lg font-medium text-gray-800">{statusMessage}</p>
                  <div className="w-full bg-gray-200 rounded-full h-4 mt-4 overflow-hidden">
                    <div className="bg-green-500 h-4 rounded-full transition-all duration-500" style={{ width: `${progress}%` }}></div>
                  </div>
                   <div className="mt-4 text-sm text-gray-600 flex justify-between">
                        {stepLabels.map((label, index) => {
                            const stepProgress = (100 / stepLabels.length) * (index);
                            return (
                                <span key={index} className={`font-medium ${progress >= stepProgress ? 'text-green-700' : 'text-gray-400'}`}>
                                    {label}
                                </span>
                            )
                        })}
                    </div>
                </div>
              )}

              {downloadUrl && (
                 <div className="w-full text-center space-y-6">
                    <CheckCircleIcon className="h-20 w-20 text-green-500 mx-auto"/>
                    <h3 className="text-3xl font-bold text-gray-900">Your Guide is Ready!</h3>
                    <a href={downloadUrl} download target="_blank" rel="noopener noreferrer" className="inline-flex items-center justify-center bg-blue-600 hover:bg-blue-700 text-white font-bold py-3 px-8 rounded-lg text-lg shadow-md hover:shadow-lg transition-transform transform hover:scale-[1.03]">
                        <ArrowDownTrayIcon className="h-6 w-6 mr-2"/>
                        Download PDF
                    </a>
                </div>
              )}

              <button onClick={resetAll} className="text-sm text-gray-600 hover:text-red-600 transition-colors pt-4">
                Start Over
              </button>
            </div>
          </div>
        </div>
      </main>
    </div>
  );
}
